 //  #include "Script::LongTerm_Script.js"

var str_jobname     = ""; // 직업명
var str_jobcod      = ""; // 직업코드
var str_jobrsk      = ""; // 직업등급
var sDriveClass     = ""; // 운전등급
var sOldJobCode     = ""; // 기존직업코드
var flag_select_yn = "N";
var flag;
var sStandDate      = "";//기준시간

	scwin.init = function() {
		scwin.Load_Cate1_List_Async();
	};

	scwin.Load_Cate1_List_Async = function() { //완료
 		util.eventAsyncCall(this, scwin.Load_Cate1_List, arguments);
	};
	scwin.Load_Cate1_List = function() {
		// 대분류 리스트 서버에서 가져옴
		ds_cate_01.removeAll();
		ds_cate_02.removeAll();
		ds_cate_03.removeAll();

		//현재시간 가져오기
		if(sStandDate == ""){
			gfn_getServerTime();
			sStandDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간
		}

/* OLD S		
		//alert(sStandDate);
		SOBJ_Job_Cate1.WSDL = gWsdlBasis;
		var MethodObj = gfn_CreateMethodObj(SOBJ_Job_Cate1,"com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeStep1", "", "");
		gfn_SetParam(MethodObj, "sStandDate", sStandDate);
		gfn_CallService(SOBJ_Job_Cate1, DOBJ_Job_Cate1, MethodObj);

		//trace(ds_cate_01.SaveXML());
OLD E */

		var MethodObj = new callService("com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeStep1", 0, 0);
		MethodObj.setId("readJobCodeStep1");
		MethodObj.setParam("sStandDate", sStandDate);
		MethodObj.setUrl("/common/CBSO021PController.do");
		MethodObj.setXPath("//*/vector/data/CODAA13");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.interfaceKey(_tranSync.result.serviceDs);
		ds_cate_01.setJSON(_tranSync.result.serviceDs.data[0].CODAA13);
	};

	scwin.grid_cate_1_oncellindexchange_Async = function() { //완료
 		util.eventAsyncCall(this, scwin.grid_cate_1_oncellindexchange, arguments);
	};
	scwin.grid_cate_1_oncellindexchange = function(row,col,oldRow,oldCol) {
		// 중분류 리스트 서버에서 가져옴
		var cate_01 = ""; // 대분류 코드 첫째 한자리
		cate_01 = ds_cate_01.getCellData(row, "sJobGroup");

		ds_cate_03.removeAll();
		ds_cate_02.removeAll();

		scwin.clear_selected_job_info();

/* OLD S		
		SOBJ_Job_Cate2.WSDL = gWsdlBasis;

		var MethodObj = gfn_CreateMethodObj(SOBJ_Job_Cate2,"com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeStep2", "", "");
		//gfn_SetParam(MethodObj, "sJobCode_cate2", cate_01);
		gfn_SetParam(MethodObj, "sJobGroup", cate_01);
		gfn_SetParam(MethodObj, "sStandDate", sStandDate);	
		gfn_CallService(SOBJ_Job_Cate2, DOBJ_Job_Cate2, MethodObj);
OLD E */

		var MethodObj = new callService("com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeStep2", 0, 0);
		MethodObj.setId("readJobCodeStep2");
		MethodObj.setParam("sJobGroup", cate_01);
		MethodObj.setParam("sStandDate", sStandDate);
		MethodObj.setUrl("/common/CBSO021PController.do");
		MethodObj.setXPath("//*/vector/data/CODAA13");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.interfaceKey(_tranSync.result.serviceDs);
		ds_cate_02.setJSON(_tranSync.result.serviceDs.data[0].CODAA13);

		ed_job_cate_1.setValue(ds_cate_01.getCellData(row, "sJobGroupNm"));

		//trace(ds_cate_02.SaveXML());
	};

	scwin.clear_selected_job_info = function() {
		// 선택 변경시 선택 항목 초기화
		ed_job_cate_2.setValue("");
		ed_job_cate_3.setValue("");
		ed_job_code.setValue("");
		ed_risk_code.setValue("");
		ed_drive_class.setValue("");
		flag_select_yn = "N";
	};

	scwin.grid_cate_2_oncellindexchange_Async = function() { //완료
 		util.eventAsyncCall(this, scwin.grid_cate_2_oncellindexchange, arguments);
	};
	scwin.grid_cate_2_oncellindexchange = function(row,col,oldRow,oldCol) {
		// 소분류 리스트 서버에서 가져옴
		var cate_02 = ""; // 중분류 코드 1~3 자리 3자리
		cate_02 = ds_cate_02.getCellData(row, "sMidJobCode");

		ds_cate_03.removeAll();

		scwin.clear_selected_job_info();

/* OLD S		
		SOBJ_Job_Cate3.WSDL = gWsdlBasis;

		var MethodObj = gfn_CreateMethodObj(SOBJ_Job_Cate3,"com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeStep3", "", "");
		gfn_SetParam(MethodObj, "sMidJobCode", cate_02);
		gfn_SetParam(MethodObj, "sStandDate", sStandDate);		
		gfn_CallService(SOBJ_Job_Cate3, DOBJ_Job_Cate3, MethodObj);
		//trace(replace(gfn_XmlEncoder(DOBJ_Job_Cate3.Value), "><", ">\n<"));
OLD E */		
		var MethodObj = new callService("com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeStep3", 0, 0);
		MethodObj.setId("readJobCodeStep3");
		MethodObj.setParam("sMidJobCode", cate_02);
		MethodObj.setParam("sStandDate", sStandDate);
		MethodObj.setUrl("/common/CBSO021PController.do");
		MethodObj.setXPath("//*/vector/data/CODAA13");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.interfaceKey(_tranSync.result.serviceDs);
		ds_cate_03.setJSON(_tranSync.result.serviceDs.data[0].CODAA13);

		ed_job_cate_2.setValue(ds_cate_02.getCellData(row, "sMidJobNm"));
	};

	scwin.grid_cate_3_oncellindexchange = function(row,col,oldRow,oldCol) {
		ed_job_cate_3.setValue(ds_cate_03.getCellData(row, "sJobCodeNm"));
		ed_job_code.setValue(ds_cate_03.getCellData(row, "sJobCode"));
		ed_risk_code.setValue(ds_cate_03.getCellData(row, "sJobClass"));
		ed_drive_class.setValue(ds_cate_03.getCellData(row, "sDriveClass"));
		ta_Job_Descript.setValue(ds_cate_03.getCellData(row, "sJobDesc"));

		str_jobname = wconv.trim(ds_cate_03.getCellData(row,"sJobCodeNm"));
		str_jobcod  = ds_cate_03.getCellData(row,"sJobCode");
		str_jobrsk  = ds_cate_03.getCellData(row,"sJobClass");
		sDriveClass = ds_cate_03.getCellData(row,"sDriveClass");
		sOldJobCode = ds_cate_03.getCellData(row,"sOldJobCode");

		flag_select_yn = "Y";
	};

	scwin.btn_ok_onclick = function(e) {
		if (flag_select_yn == "Y")
		{
			var msg  = "'부모창에 넘길 값(부모창에 변수 선언하여 전달 바랍니다.)'\n";
				msg += "직업명은 [" + str_jobname + "] 이고,\n";
				msg += "직업코드는 [" + str_jobcod + "] 이고,\n";
				msg += "직업등급은 [" + str_jobrsk + "] 이고,\n";
				msg += "운전등급은 [" + sDriveClass + "] 이고,\n";
				msg += "기존직업코드는 [" + sOldJobCode + "] 입니다.";

			//alert(msg);

			// 직업정보 넘겨줌.
			// 직업명|직업코드|상해등급|운전등급
			var returnMsg = str_jobname + "^" + str_jobcod + "^" + str_jobrsk + "^" + sDriveClass + "^" + sOldJobCode;

			//A230_Pop.Close(returnMsg);
			wprom.close(returnMsg);
		}
		else if (flag_select_yn == "N")
		{
			wprom.alert("직업을 선택해 주세요.");
		}
	};

	scwin.btn_retrieve_onclick_Async = function() { //완료
 		util.eventAsyncCall(this, scwin.btn_retrieve_onclick, arguments);
	};
	scwin.btn_retrieve_onclick = function(e) {
		// 이름으로 직업 검색
		if (ed_jobname.getValue() == "")
		{
			wprom.alertF("직업명을 입력하세요.", {}, ed_jobname);
			return;
		}

		var jobname = "";
		jobname = wconv.trim(ed_jobname.getValue());

		ds_search_job.removeAll();

/* OLD S		
		SOBJ_Search_Job.WSDL = gWsdlBasis;

		var MethodObj = gfn_CreateMethodObj(SOBJ_Search_Job,"com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeList", "", "");

		gfn_SetParam(MethodObj, "sSearchType", "1");
		gfn_SetParam(MethodObj, "sKeyType", "3");
		gfn_SetParam(MethodObj, "sKeyText", jobname);
		gfn_SetParam(MethodObj, "sStandDate", sStandDate);		

		gfn_CallService(SOBJ_Search_Job, DOBJ_Search_Job, MethodObj);
OLD E */		
		var MethodObj = new callService("com.inswave.newgen.com.cod.OCJ20200300Task", "readJobCodeList", 0, 0);
		MethodObj.setId("readJobCodeList");
		MethodObj.setParam("sSearchType", "1");
		MethodObj.setParam("sKeyType", "3");
		MethodObj.setParam("sKeyText", jobname);
		MethodObj.setParam("sStandDate", sStandDate);
		MethodObj.setUrl("/common/CBSO021PController.do");
		MethodObj.setXPath("//*/vector/data/CODAA13");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.interfaceKey(_tranSync.result.serviceDs);
		ds_search_job.setJSON(_tranSync.result.serviceDs.data[0].CODAA13);

		if (ds_search_job.getRowCount() == 0)
		{
			wprom.alert("검색 결과가 없습니다.");
		}

		scwin.tab_1_tab2_grid_cate_3_oncellclick(0, "");

		//trace(ds_search_job.SaveXML());
	};

	scwin.tab_1_tab2_grid_cate_3_oncellclick = function(row,col) {
		ed_job_cate_3.setValue(ds_search_job.getCellData(row, "sName3"));
		ed_job_code.setValue(ds_search_job.getCellData(row, "sJobCode"));
		ed_risk_code.setValue(ds_search_job.getCellData(row, "sJobClass"));
		ed_drive_class.setValue(ds_search_job.getCellData(row, "sDriveClass"));
		ta_Job_Descript.setValue(ds_search_job.getCellData(row, "sJobDesc"));

		str_jobname = wconv.trim(ds_search_job.getCellData(row,"sName3"));
		str_jobcod  = ds_search_job.getCellData(row,"sJobCode");
		str_jobrsk  = ds_search_job.getCellData(row,"sJobClass");
		sDriveClass = ds_search_job.getCellData(row,"sDriveClass");
		sOldJobCode = ds_search_job.getCellData(row,"sOldJobCode");

		flag_select_yn = "Y";

		ed_job_cate_1.setValue(ds_search_job.getCellData(row, "sName1"));
		ed_job_cate_2.setValue(ds_search_job.getCellData(row, "sName2"));
	};

	scwin.ed_jobname_onkeydown = function(e) {
		if (e.keyCode == 13) {
			scwin.btn_retrieve_onclick_Async();
		}
	};

	scwin.btn_close_onclick = function(e) {
		wprom.close();
	};
