var gsSysDate;			//global 현재일자
var gsDate;				//global 현재일자
var gsDateYYYYMM	= 	"";
var gsFromDate 		=	"";
var gsToDate		=	"";
var gsTime			=	"";
var gUserID; 			//global 사용자 ID
var gUserEmpName; 	    //global 부서명
var gUserDuty;			//global UserDuty

//이미지시스템
var gsImageSysUrlR	=	"https://limg.thekins.co.kr:1443/imgsys/jsp/index.jsp?";
var gsImageSysUrlD	=	"http://10.1.1.131:8090/imgsys/jsp/index.jsp?";

