//홈페이지
var gsHomepgSysUrl	=	"http://www.educar.co.kr";


//**********************************************************
// 초기화
//**********************************************************
scwin.init = function () {
	//call 로드 function
	scwin.fnFrstScrnMngmLoad();	
};

//**********************************************************
//로드 function
//**********************************************************
scwin.fnFrstScrnMngmLoad = function() {
	util.gfn_getServerTime();
	gsSysDate		= wconv.substr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8);
	gsDateYYYYMM 	= wconv.mid(gsSysDate, 0, 6);
	gsFromDate 		= wconv.mid(gsSysDate, 0, 6) + "01";
	gsToDate 		= wconv.mid(gsSysDate, 0, 6) + util.getLastDay(gsDateYYYYMM);
	gsTime 			= wconv.mid(gsSysDate, 8, 6);

	gUserID 		= util.getGdsValue("gUserID");    					//global 사용자 ID
	gUserEmpName 	= util.getGdsValue("gUserEmpName");    				//global 부서명
	gUserDuty		= util.getGdsValue("gUserDuty");					//global UserDuty

	//call 월실적현황(infterface)
	scwin.fnRetrieveNwcrPrreList();

	//call 초기 화면(품의결과(심사진행현황)) 검색
	scwin.fnRetrieveJugmCrst();

	//call 초기 화면(완전판매상태(월별 완전판매 모니터링 현황)) 검색
	scwin.fnRetrieveCompleteSalesList();

	//로그인ID권한조회(공지사항 조회를 위한 권한조회)
	scwin.getLginAuthInfo();
};

//**********************************************************
//Fusion Chart Data Load(신계약속보 selectNwcrPrreList 호출)
//**********************************************************
scwin.fnRetrieveNwcrPrreList = function() {
	//초기화
    wconv.clear(warning);
	wconv.clear(Ds_View);
	wconv.clear(Ds_List);

	var sOrgnFlag = "2";
	if(gUserDuty == "30" || gUserDuty == "33" || gUserDuty == "35") {
		sOrgnFlag = "3";
	}

	var MethodObj = new callService("com.inswave.sales.sam.task.NwcrPrreTask", "selectNwcrPrreList", "", ""); 
	MethodObj.setId("selectNwcrPrreList");
    MethodObj.setParam("sFromDate", 	gsFromDate);//조회기간 년월일 시작
    MethodObj.setParam("sToDate", 		gsToDate);	//조회기간 년월일 종료
    MethodObj.setParam("sInqrFlag", 	"2");	//조회구분 1:조직별 2:상품별
	MethodObj.setParam("display", 		"1"); 	//화면형태 1:납입주기 2:입금방법
	MethodObj.setParam("sOrgnFlag", 	sOrgnFlag);	//조직,사용자 구분 2:취급자 3:사용인
	MethodObj.setParam("sOrgnCd", 		gUserID);	//구분 코드
	MethodObj.setParam("sGdGroupCd", 	"");	//상품군
	MethodObj.setParam("sInskd", 		"1");	//보종구분 1:장기 2:일반
	MethodObj.setParam("sInqrCond", 	"3");	//조회조건 3:신계약-철회취소민원해지
	MethodObj.setParam("sGdCd", 		"");	//상품코드
	MethodObj.setUrl("/contract/CBSC180MController.do");
	MethodObj.setXPath("//*/CCCBF00");	
	MethodObj.addXPath("//WARNING");
	MethodObj.callBack("scwin.subCallBackfunction");
	MethodObj.async();
	MethodObj.send();
};

//**********************************************************
//초기 화면(품의결과(심사진행현황)) 검색
//**********************************************************
scwin.fnRetrieveJugmCrst = function() {
	FrstScrnMngmVo.set("stndDate", 	gsSysDate);
	FrstScrnMngmVo.set("startDate", gsSysDate);
	FrstScrnMngmVo.set("endDate", 	gsSysDate);

	if(gUserDuty == "30" || gUserDuty == "33" || gUserDuty == "35") {
		FrstScrnMngmVo.set("doerUser", 	gUserID);
	} else {
		FrstScrnMngmVo.set("doer", 		gUserID);
	}

	var trans = new transSub();						// transaction 보낼 객체 생성
	trans.setId("retrieveJugmCrstList");			// transaction 보낼 SubmissionID
	trans.setUrl("/common/CBSO0005.do");			// Controller 쪽으로 보낼 주소
	trans.setDs("FrstScrnMngmVo");
	trans.targetDs("FrstScrnMngmListVo");
	trans.callBack("scwin.subCallBackfunction");	// 받을 Dataset을 처리시 콜백함수
	trans.async();
	trans.send();
}; 

//**********************************************************
//초기 화면(완전판매상태(월별 완전판매 모니터링 현황)) 검색-완전판매모니터링명세(/CBSB021M.xml)
//**********************************************************
scwin.fnRetrieveCompleteSalesList = function() {
	//초기화
	wconv.clear(DsWs_totCnt);
    wconv.clear(DsWs_search);
    wconv.clear(DsWs_SearchWarning);

	var MethodObj = new callService("com.inswave.ccc.task.CSStatisticsTask", "getCompleteSalesList", 500, 1);
	MethodObj.setId("getCompleteSalesList");
 	MethodObj.setParam("nKind", "2");		//모집조직 2:사번
 	MethodObj.setParam("sEmpNo", gUserID);
 	MethodObj.setParam("sStatus", "all");	//상태 all:전체
	MethodObj.setParam("sType", "all");		//보종 all:전체
	MethodObj.setParam("sTypeDetail", "all");	//세부보종 all:전체
	MethodObj.setParam("sKey", "all");	//검색어조건 all:전체
	MethodObj.setParam("sWord", "");	//검색어	
	MethodObj.setParam("sIncompletGubun", "1");	//불완전사유
	MethodObj.setParam("sEtcChk", "");		//기타포함
	MethodObj.setParam("sIncomChk", "Y");	//불완전기타 전체(1)로 세팅
	MethodObj.setParam("sFromDate", gsFromDate);
	MethodObj.setParam("sToDate", gsToDate);
	MethodObj.setParam("more65YearsYN", "");
	MethodObj.setParam("sCustType", "");
	MethodObj.setParam("sPersonalInfo", "");	// 개인정보적법성
	MethodObj.setUrl("/common/retrieveCompleteSalesList.do");
	MethodObj.setXPath("//*/hashtable/data/vector/data/CCCMA01Doc");
	MethodObj.addXPath("//*/vector");  
	MethodObj.addXPath("//*/WARNING");
	MethodObj.callBack("scwin.subCallBackfunction");
	MethodObj.async();
	MethodObj.send();
};

scwin.subCallBackfunction = function(e) {
	switch(e.subID){
		// 초기화 fusion chart(신계약정보) 검색
		case "selectNwcrPrreList":
			//Set Fusion Chart Option
			var options = { caption : "",
				subCaption : "",
				yAxisName : "",
				formatnumberscale: "0",
				yAxisValueDecimals:"0",
				yAxisMinValue:"0", 
				yAxisMaxValue:"10",
				xAxisName : "",
				numberPrefix:"" };
			mfct_selectNwcrPrreList.setChartAttribute(options);

			//Get Return Value
			util.jSonDsCreate(_tranSync.getDsJson("CCCBF00"), DsWs_CCCEF00.getID());
			if(DsWs_CCCEF00.getRowCount() > 0) {
				for(var i=0;i<DsWs_CCCEF00.getRowCount();i++) {
					if (DsWs_CCCEF00.getCellData(i, "sLEVEL") == "1") {
						var sEnsrSaleCnt 		= util.appendComma(DsWs_CCCEF00.getCellData(i, "nGurntCt")); 
						var sSaveSaleCnt 		= util.appendComma(DsWs_CCCEF00.getCellData(i, "nSviCt")); 
						var sEnsrPremSum 		= util.appendComma(DsWs_CCCEF00.getCellData(i, "nGurntStotAmt")); 
						var sSavePremSum 		= util.appendComma(DsWs_CCCEF00.getCellData(i, "nSviStotAmt")); 
						var sEnsrMpayPremSum 	= util.appendComma(DsWs_CCCEF00.getCellData(i, "nGurntMpayCnvsAmt")); 
						var sSaveMpayPremSum 	= util.appendComma(DsWs_CCCEF00.getCellData(i, "nSviMpayCnvsAmt")); 

						msta_nEnsrSaleCnt.setValue(sEnsrSaleCnt);
						msta_nSaveSaleCnt.setValue(sSaveSaleCnt);
						msta_nEnsrPremSum.setValue(sEnsrPremSum);
						msta_nSavePremSum.setValue(sSavePremSum);
						msta_nEnsrMpayPremSum.setValue(sEnsrMpayPremSum);
						msta_nSaveMpayPremSum.setValue(sSaveMpayPremSum);
					} else if (DsWs_CCCEF00.getCellData(i, "sLEVEL") == "3") {
						util.appendRow(DsWs_List);

						var currRow = DsWs_List.getRowCount()-1;
						wconv.mi_copyRow(DsWs_CCCEF00.getID(), i, DsWs_List.getID(), currRow);
					}
				}
			}
			else {
				//월별실적현황(default)
				msta_nEnsrSaleCnt.setValue("0");
				msta_nSaveSaleCnt.setValue("0");
				msta_nEnsrPremSum.setValue("0");
				msta_nSavePremSum.setValue("0");
				msta_nEnsrMpayPremSum.setValue("0");
				msta_nSaveMpayPremSum.setValue("0");

				//chart(default)
				util.appendRow(DsWs_List);

				DsWs_List.setCellData(0, "nGurntCt",	0);
				DsWs_List.setCellData(0, "sDeptName",	" ");
			}

			//bind Fusion Chart Load
			util.setRowPosition(DsWs_List, 0);
		break;

		//품의결과(심사진행현황) 검색
		case "retrieveJugmCrstList":
			if(e.flag == "s") {
				dsl_jugmCrstList.setJSON(e.result.listfrstScrnMngmVo);

				//bind 초기 화면(품의결과(심사진행현황)) 검색
				util.setRowPosition(dsl_jugmCrstList, 0);
			}else {
				//wprom.alert(e.msg);
			}
		break;	

		//완전판매상태(월별 완전판매 모니터링 현황)) 검색
		case "getCompleteSalesList":
			//	util.interfaceKey(_tranSync.result.serviceDs);
			//console.log(JSON.stringify(_tranSync.result));
			dsl_completeSalesList.setJSON(_tranSync.result.serviceDs.listfrstScrnMngmVo);	// dataset 데이터//

			//bind 초기 화면(완전판매상태(월별 완전판매 모니터링 현황)) 검색
			//util.setRowPosition(DsWs_search, 0);
			util.setRowPosition(dsl_completeSalesList, 0);
			//msta_nCompPectSaleMntrCrstCnt.getValue();
		break;

		// 로그인ID권한조회
		case "retrieveLginIDAuthInqr":
			if(e.flag == "s") {
				//console.log("main retrieveLginIDAuthInqr: " + e.result);
				dst_LginAuthInqrVo.setJSON(e.result);

				scwin.getNotcMattList();
			}else {
				//wprom.alert(e.msg);
			}															
		break;

		//공지사항조회
		case "retrieveNotcMattList":
			if(e.flag == "s") {
				dst_NotcMattInfoListVo.setJSON(e.result.notcMattInfoVo);

				//bind 공지사항 조회
				util.setRowPosition(dst_NotcMattInfoListVo, 0);

				//generator setting
				if(dst_NotcMattInfoListVo.getRowCount() > 0) {
					for(var i=0;i<dst_NotcMattInfoListVo.getRowCount();i++) {
						var noticMattName	=	dst_NotcMattInfoListVo.getCellData(i, "noticMattName");
						var rgstDate		=	wconv.dateformat(dst_NotcMattInfoListVo.getCellData(i, "rgstDate"), "yyyy-MM-dd");
						//console.log(noticMattName + " : " + rgstDate);

						if(dst_NotcMattInfoListVo.getRowCount() < 2) {
							grd_generator.insert();
							grd_generator.getChild(i, "noticMattName").setValue(noticMattName ? noticMattName : "Not Found Data");
					  		grd_generator.getChild(i, "rgstDate").setValue("");
					  	} else {

							if(i < 5) {
								if((noticMattName == null && noticMattName == "")
									&& (rgstDate == null && rgstDate == ""))
								{
									grd_generator.insert();
									grd_generator.getChild(i, "noticMattName").setValue(noticMattName ? noticMattName : "Not Found Data");
							  		grd_generator.getChild(i, "rgstDate").setValue("");
								} else {
									grd_generator.insert();
									grd_generator.getChild(i, "noticMattName").setValue(noticMattName ? noticMattName : "Not Found Data");
							  		grd_generator.getChild(i, "rgstDate").setValue(rgstDate ? rgstDate : "");
							  	}
							}
						}
					}
				} else {
					grd_generator.insert();
					grd_generator.getChild(0, "noticMattName").setValue("Not Found Data");
			  		grd_generator.getChild(0, "rgstDate").setValue("");
			  	}

			} else {
				//wprom.alert(e.msg);
			}														
		break;
	}
};

//**********************************************************
// 로그인자 권한조회 /common/CBSO005.do 로그인자 권한조회
//**********************************************************
scwin.getLginAuthInfo = function() {
	dst_LginAuthInqrVo.set("empNo" , gUserID);

	var trans = new transSub();                  	// transaction 보낼 객체 생성
	trans.setId("retrieveLginIDAuthInqr");        	// transaction 보낼 SubmissionID
	trans.setUrl("/common/CBSO0015.do");   			// Controller 쪽으로 보낼 주소
	trans.setDs("dst_LginAuthInqrVo");					
	trans.targetDs("dst_LginAuthInqrVo");		
	trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
	trans.async();
	trans.send();
};

//**********************************************************
// 공지사항리스트조회(초기 화면(공지사항) 검색) - fnRetrieveNotcFrst
//**********************************************************
scwin.getNotcMattList = function() {
	// INPUT DS 세팅					
	dst_NotcMattInfoVo.set("rgtrEmpNo" , 		gUserID);	//조회사원번호
	dst_NotcMattInfoVo.set("noticDate", 		gsFromDate); //"20171030"); //gsSysDate); //게시일자
	dst_NotcMattInfoVo.set("noticEnddt", 		gsToDate);	//종료일자
	dst_NotcMattInfoVo.set("noticMattFlag", 	"");		//공지사항구분
	dst_NotcMattInfoVo.set("noticMattName", 	"");		//공지사항명
	dst_NotcMattInfoVo.set("noticMattCont", 	"");		//공지사항내용

	if(dst_LginAuthInqrVo.get("bizAdmrYn") == "Y"){
		dst_NotcMattInfoVo.set("drctMangAuthYn", "");//직영권한
		dst_NotcMattInfoVo.set("crossAuthYn", "");//교차권한
		dst_NotcMattInfoVo.set("atchAuthYn", "");//전속권한
		dst_NotcMattInfoVo.set("gAAuthYn", "");	//ga권한

	}else{
		dst_NotcMattInfoVo.set("drctMangAuthYn", dst_LginAuthInqrVo.get("drctMangYn"));//직영권한
		dst_NotcMattInfoVo.set("crossAuthYn", dst_LginAuthInqrVo.get("crossYn"));//교차권한
		dst_NotcMattInfoVo.set("atchAuthYn", dst_LginAuthInqrVo.get("atchAuthYn"));//전속권한
		dst_NotcMattInfoVo.set("gAAuthYn", dst_LginAuthInqrVo.get("gAYn"));	//ga권한		
	}

	var trans = new transSub();                  	// transaction 보낼 객체 생성
	trans.setId("retrieveNotcMattList");        	// transaction 보낼 SubmissionID
	trans.setUrl("/common/CBSO0009.do");			// Controller 쪽으로 보낼 주소
	trans.setDs("dst_NotcMattInfoVo");				
	trans.targetDs("dst_NotcMattInfoListVo");		
	trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
	trans.async();
	trans.send();
};

//메뉴 OPEN 링크
scwin.openLink_onclick = function(arg){
	gfn_OpenLinkPage(arg); // 넘어온 화면ID를 OPEN한다.
};


//공지사항 상세창 OPEN
scwin.openNotiDetailLink_onclick = function() {
	//debugger;
	//alert(this.getGeneratedIndex());
	//console.log(row);
	var row = this.getGeneratedIndex();
	var sNoticMattId = dst_NotcMattInfoListVo.getCellData(row, "noticMattId"); 
	//console.log(sNoticMattId);	
	var promise = Promise.resolve();
    promise = promise.then(function(rtnVal) {
    	scwin.openPopupCBSO006P(sNoticMattId);
	});
};
//**********************************************************
// 공지사항등록[CBSO006P]팝업화면 오픈
//**********************************************************
scwin.openPopupCBSO006P = function(sNoticMattId) {
	var promise = Promise.resolve();	        
	promise = promise.then(function(rtnVal) {
		var indata  = {"noticMattId" 	: sNoticMattId};
		var popInfo = {
		    "width"     : 740,
		    "height"    : 340,
		    "top"       : 100,
		    "left"      : 100,
		    "indata"    : indata,
		    //"callback"  : scwin.popupCallback,
		    "title"     : "공지사항 등록 및 조회"
		};
		return wprom.popupOpen("/ui/cbso/CBSO006P.xml", popInfo);
	});
};

//NEW OPEN 링크
scwin.newLink_onclick = function(arg){
	//window.open(arg, "_blank", ""); // 넘어온 URL을 NEW OPEN한다.
	if(arg == "ImageSys") {
		if (window.top.gds.gDs_Resource.getCellData(0, "gProfile") == "prod") {
			window.open(gsImageSysUrlR, "_blank", "toolbar=yes, menubar=yes, scrollbars=yes, location=yes, resizable=yes");
		} else {
			window.open(gsImageSysUrlD, "_blank", "toolbar=yes, menubar=yes, scrollbars=yes, location=yes, resizable=yes");
		}
	}
	if(arg == "BojangSys") {
		window.open(gsBojangSysUrl, "_blank", "toolbar=yes, menubar=yes, scrollbars=yes, location=yes, resizable=yes");
	}
	if(arg == "HomepgSys") {
		window.open(gsHomepgSysUrl, "_blank", "toolbar=yes, menubar=yes, scrollbars=yes, location=yes, resizable=yes");
	}

};
