head.script:
var handlerID = "";
var standDate;

	scwin.init = function() {
		handlerID = util.getGdsValue("gUserID");

		gfn_getServerTime();
		standDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간
		standDate = standDate.substring(0,8);

		gfn_btnEnable(false, false, false, false, false, false, false, true);

		var json = gfn_getJsonCommonCode("GdCdLink");
	    if(json != null){
	    	DsWs_GdCdLink.setJSON(json, false);
	    }
	};

	scwin.btn_Search_onclick = function() {
		PossCstMngmVo.set("handlerID", handlerID);

		if (id_searchGubun.getValue() == "01") {
			PossCstMngmVo.set("name", id_searchValue.getValue());
			PossCstMngmVo.set("custNo", "");
		} else if (id_searchGubun.getValue() == "02") {
			PossCstMngmVo.set("name", "");

			var custNo = "";
			if (id_searchValue.getValue() > "") {
				custNo = scwin.lfn_getCustNo(id_searchValue.getValue());
				if (custNo == "") {
					wprom.alert("등록되지 않은 고객입니다.");
					return;
				}
			}
			PossCstMngmVo.set("custNo", custNo);
		}

		var trans = new transSub();						// transaction 보낼 객체 생성
		trans.setId("retrievePossCstList");				// transaction 보낼 SubmissionID
		trans.setUrl("/common/CBSO0001.do");			// Controller 쪽으로 보낼 주소
		trans.setDs("PossCstMngmVo");
		trans.targetDs("PossCstListVo");
		trans.callBack("scwin.subCallBack");			// 받을 Dataset을 처리시 콜백함수
		trans.send();
	};

	scwin.subCallBack = function(e) {
		switch(e.subID){
			case "retrievePossCstList":
				if (e.flag == "s") {
					PossCstListVo.setJSON(e.result.listPossCstMngmVo);
				} else {
					wprom.alert(e.msg);
				}
				break;
		}
	};

	scwin.grd_possCst_oncelldblclick = function(row,col) {
		scwin.custPopupOpen(row);
	};

	scwin.custPopupOpen = function(currRow) {
		var pro = Promise.resolve();

		var parentInfo = "";
		parentInfo = grd_possCst.getCellDisplayData(currRow, "custNo")+"^"; //고객ID
		parentInfo += grd_possCst.getCellDisplayData(currRow, "custNo")+"^";//주민번호
		parentInfo += "0^";//순번
		parentInfo += "1^";//고객유형 내국인
		parentInfo += "^";//주소구분코드 자택
		parentInfo += "^";//주소변경회차
		parentInfo += "^";//전화회차
		parentInfo += "^";//발송지주소구분
		parentInfo += "^";//발송지회차
		parentInfo += "Ds_long_mPlr";//데이터셋명 

		pro = pro.then(function(rv) {
			var indata  = {"parentInfo" : parentInfo,
			               "screenId" : "long",
			               "sStandDate" : standDate,
			               "fAgntFlagPop" : "",
			               "sPlanNo" : "",
			               "sHandlerID" : handlerID};

			var popInfo = {
				"width"     : 800,
				"height"    : 580,  
				"indata"    : indata,
				"title"     : "개인고객등록",
				"callback"  : scwin.popupCallback};

			return wprom.popupOpen("/ui/cbso/CBSO002P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();

			if (rtnValue.Ds_long_mPlr != "") {
				PossCstListVo.setCellData(currRow, "name", rtnValue.Ds_long_mPlr[0].sName);

				var cellPhone1;
				if (wconv.length(rtnValue.Ds_long_mPlr[0].sCellPhone1) > 0) {
					cellPhone = rtnValue.Ds_long_mPlr[0].sCellPhone1 + "-"+
								rtnValue.Ds_long_mPlr[0].sCellPhone2 + "-****";
				} else {
					cellPhone = rtnValue.Ds_long_mPlr[0].sCellPhone1 +
								rtnValue.Ds_long_mPlr[0].sCellPhone2;
					if (wconv.length(rtnValue.Ds_long_mPlr[0].sCellPhone3) > 0) {
						cellPhone = cellPhone+"****";
					}
				}

				PossCstListVo.setCellData(currRow, "cellPhone", cellPhone);
				PossCstListVo.setCellData(currRow, "email1" , rtnValue.Ds_long_mPlr[0].sEmail1);
				PossCstListVo.setCellData(currRow, "adrs", rtnValue.Ds_long_mPlr[0].sHomAdr1);

				PossCstListVo.setCellData(currRow, "newJobCode", rtnValue.Ds_long_mPlr[0].sJobCode);
				PossCstListVo.setCellData(currRow, "newJobName" , rtnValue.Ds_long_mPlr[0].sJobName);
				PossCstListVo.setCellData(currRow, "marry", rtnValue.Ds_long_mPlr[0].sMarry);
			}

			if (rtnValue.cusaa12 != "") {
				PossCstListVo.setCellData(currRow, "birthDate", rtnValue.cusaa12[0].sBirthDate);
				PossCstListVo.setCellData(currRow, "birthType", rtnValue.cusaa12[0].sBirthType);
				PossCstListVo.setCellData(currRow, "weddingDate", rtnValue.cusaa12[0].sWeddingDate);
				PossCstListVo.setCellData(currRow, "anvsDate", rtnValue.cusaa12[0].sAnvsDate);
				PossCstListVo.setCellData(currRow, "chldYn", rtnValue.cusaa12[0].sChidYn);
				PossCstListVo.setCellData(currRow, "etcMemo", rtnValue.cusaa12[0].sEtcMemo);
			}
		});
	}

	scwin.grd_possCst_oncellclick = function(row,col) {
		var colName = grd_possCst.getColumnID(col);
		if (colName == "sel") {
			grd_possCst.checkAll("sel", false);
			grd_possCst.setCellChecked(row, col, true);
		} else if (colName == "imgEtcMemo") {
			scwin.etcMemoPopupOpen(row);
		}
	};

	scwin.etcMemoPopupOpen = function(currRow) {
		var custNo = PossCstListVo.getCellData(currRow , "custNo");
		var etcMemo = PossCstListVo.getCellData(currRow , "etcMemo");

		var pro = Promise.resolve();

		pro = pro.then(function(rv) {
			var indata  = {"sCustNo" : custNo,
						   "handlerID" : handlerID
						   };

			var popInfo = {
				"width"     : 600,
				"height"    : 230,  
				"indata"    : indata,
				"title"     : "고객메모관리",
				"callback"  : scwin.popupCallback
			};

			return wprom.popupOpen("/ui/cbso/CBSO030P.xml", popInfo);
		});

		pro = pro.then(function(rv) {
			var rtnValue = wprom.getReturnData();

			if (rtnValue != "") {
				PossCstListVo.setCellData(currRow, "etcMemo", rtnValue.etcMemo);
			}
		});
	};

	scwin.id_searchGubun_onviewchange = function(info) {
		id_searchValue.setValue("");
	};

	scwin.btn_NewHealth_onclick = function() {
		scwin.winlink("21", "에듀플러스", "00", "");
	};

	scwin.btn_NewDrive_onclick = function() {
		scwin.winlink("23", "운전자", "01", "연만기");
	};

	scwin.btn_New03_onclick = function() {
		scwin.winlink("29", "법률비용", DsWs_Goods.getCellData(0, "sGnrzCd"), "");
	};

	scwin.btn_NewPrprt_onclick = function() {
		scwin.winlink("25", "재물", "00", "");
	};

	scwin.btn_NewAnntChung_onclick = function() {
		scwin.winlink("27", "연금", "", "");
	};

	scwin.btn_NewChung_onclick = function() {
		//신규청약
		var selRow = grd_possCst.getCheckedIndex("sel");
		if (selRow == "") {
			wprom.alert("고객을 선택하세요.");
			return;
		}

		var sVvalue = scwin.lfn_getRealNo(PossCstListVo.getCellData(selRow, "custNo"));//주민번호를 가져온다.

		var oDsJson = [{"sInsuredCode" : sVvalue}];

		window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");
		gfn_OpenLinkPage("CBSC015M");
	};

	scwin.winlink = function(btnGubun, btnName, seqNo, jobType) {
		var selRow = grd_possCst.getCheckedIndex("sel");
		if (selRow == "") {
			wprom.alert("고객을 선택하세요.");
			return;
		}

		if (gfn_isCA(util.getGdsValue("gUserID"), util.getGdsValue("gUserDuty"))){
			wpro.alert("현재 상담그룹은 가입설계가 불가합니다.");
			return;
		}

		var sGdCd = DsWs_GdCdLink.getMatchedColumnData("sCd", btnGubun, "sHnglCdName", true, 0, DsWs_GdCdLink.getRowCount());

		var MethodObj = new callService("com.inswave.longterm.prd.rle.task.BCA710Task", "getInsAvaGdList", 0, 0);
		MethodObj.setId("getInsAvaGdList");
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setParam("sResno", PossCstListVo.getCellData(selRow, "custNo"));
		MethodObj.setParam("sGdCd", sGdCd);
		MethodObj.setParam("sUserID", handlerID);
		MethodObj.setXPath("//*/hashtable/data/vector/data/LTIEA00");
		MethodObj.addXPath("//*/hashtable/data/LTIEA00Count");
		MethodObj.addXPath("//*/vector/data/multi");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		util.interfaceKey(_tranSync.result.serviceDs);
		DsWs_CntrList.setJSON(_tranSync.getDsJson("LTIEA00"));
		DsWs_Cnt.setJSON(_tranSync.getDsJson("LTIEA00Count"));
		DsWs_Goods.setJSON(_tranSync.getDsJson("multi"));

	    if(DsWs_Goods.getRowCount() == 0){
			wprom.alert(btnName+ "보험 가입이 불가능한 고객입니다.");
			return false;
		}

		var sVvalue = scwin.lfn_getRealNo(PossCstListVo.getCellData(selRow, "custNo"));//주민번호를 가져온다.

		var bunsMetdDocuClsfCd = "";
		var openWinId = "CBSC015M";
		if (btnGubun == "25") {
			openWinId = "CBSC094M";	//재물
		} else if (btnGubun == "27") {
			openWinId = "CBSC075M";	//연금
			bunsMetdDocuClsfCd = "7";
		}	          

		var oDsJson = [{"sInsuredCode" : sVvalue,
						"sTitle": DsWs_Goods.getCellData(0, "sGdCd"),
						"sContents": DsWs_Goods.getCellData(0, "sGdName"),
						"sSeqNo": seqNo,
						"sJobType" : jobType,
						"sBunsMetdDocuClsfCd" : bunsMetdDocuClsfCd
					  }];

		gds.gDs_LinkArg.setJSON(oDsJson);
		gfn_OpenLinkPage(openWinId);
	};

//---------------------------------------------------------------------------
// function 명 : lfn_getRealNo (기간계B370)
// 내용        : 실주민번호 조회
//---------------------------------------------------------------------------
	scwin.lfn_getRealNo = function(sCustKeyNo) {
		DsWs_CustNo.removeAll();

		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectRealCustNo", 0, 0);
		MethodObj.setId("selectRealCustNo");
		MethodObj.setParam("sCustKeyNo", sCustKeyNo);
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setXPath("//*/LTI0000");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		DsWs_CustNo.setJSON(_tranSync.getDsJson("LTI0000"));

		if(DsWs_CustNo.getRowCount() > 0 && wconv.length(DsWs_CustNo.getCellData(0,"sRealCustNo")) >  0) {
			return DsWs_CustNo.getCellData(0,"sRealCustNo");
		}else{
			return sCustKeyNo;
		}
	};

//---------------------------------------------------------------------------
// function 명 : lfn_getCustNo
// 내용        : 고객번호 조회
//---------------------------------------------------------------------------
	scwin.lfn_getCustNo = function(sRealCustNo) {
		DsWs_CustNo.removeAll();

		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectCustKeyNo", 0, 0);
		MethodObj.setId("selectCustKeyNo");
		MethodObj.setParam("sRealCustNo", sRealCustNo);
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setXPath("//*/LTI0000");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		DsWs_CustNo.setJSON(_tranSync.getDsJson("LTI0000"));

		if(DsWs_CustNo.getRowCount() > 0 && wconv.length(DsWs_CustNo.getCellData(0,"sCustKeyNo")) >  0) {
			return DsWs_CustNo.getCellData(0,"sCustKeyNo");
		}else{
			return "";
		}
	};
