var pageSize = "15";
var pageCount = "5";
var gsSysDate;
var args;
var psButton = "버튼";
var pCode = "";		// 코드
var bOpenSearchFlag = true;	// 오픈시 자동조회 여부

//---------------------------------------------------------------------------
// function 명 : lfn_OnLoadCompleted
// 내용        : 폼로드할때 처리할 내용 
//---------------------------------------------------------------------------
scwin.init = function() {
	console.log("[/CBSO033P.xml] [init()] ==> [초기화]" );

	var indata = $w.getParameter("param"); 
	// console.log("[/CBSO033P.xml] [init()] ==> [indata]"+ indata );

	try {
		args = JSON.parse(indata);		// 부모창에서 받아온 값( 예) args.tab)
	} catch(e) {}

	bOpenSearchFlag = true;

	//sEmpNoPop.focus();		// 사원코드 포커스
	util.focus(sEmpNoPop); 	 // 사원코드 포커스 설정 

    // 초기화
	scwin.form_init();

	// 조회
    if(psButton != "버튼") {
		scwin.getSearchList();
	}

	if(!bOpenSearchFlag) {
		return;
	}

	bOpenSearchFlag = false;  

	//console.log("[/[/CBSO033P.xml] [init()] ==> [사원코드]"+ sEmpNamePop.getValue() ); 
	//	sEmpNamePop.setValue("정진");   // 사원명	// TEST @@@ ===>
};

scwin.form_init = function() { 
    scwin.initialize(); 

    sResignGubun.setValue("Y");		// 퇴직여부(Y: 재직)  
};

/* ***************************************** */
//화면 초기화
/* ***************************************** */
scwin.initialize = function(e) {
	// console.log("[/[/CBSO033P.xml] [initialize()] ==> [화면 초기화]" );

    sEmpNamePop.setValue("");
    sEmpNoPop.setValue("");
    sDepartNamePop.setValue("");
    sResignGubun.setValue("");		// 퇴직여부(Y: 재직) 
};

/* ***************************************** */
// 조회 버튼 클릭시
/* ***************************************** */
scwin.btn_search_Onclick  = function(e) {
	//console.log("[/CBSC090M.xml] [btn_search_Onclick()] ==> [조회 버튼 클릭 이벤트] [TEST_01]");  

	util.eventAsyncCall(this, scwin.getSearchList, arguments);		// 비동기 방식 호출 	
};

/* ***************************************** */
// 비동기 방식 호출 	 	
/* ***************************************** */
scwin.getSearchList = function(e) {
 	//console.log("[/CBSO033P.xml] [getSearchList()] ==> [조회 버튼 클릭 이벤트] [TEST_01]" );

	scwin.Search("1");		// 조회 

    var sTotCnt = util.appendComma(Ds_TotCnt.getCellData(0, "totalRows")); 	// 전체 건수(3자리 콤마) 

    // 전체 건수 얻어오기(Paging 처리)
	gfn_MakePagingData(Ds_Page, Ds_TotCnt.getCellData(0, "totalRows"), "10", "10"); 

    Cmb_Page.setValue("1"); //----- End

	totalRows_02.setValue(sTotCnt);	// 전체 갯수 DS 
};

/* ***************************************** */
// 조회
/* ***************************************** */
scwin.Search = function(pageNo) {
	//console.log("[/[/CBSO033P.xml] [doSearch()] ==> [조회]" );

 	var MethodObj = new callService("com.inswave.accounting.task.AccountingCommonTask", "selectEmpInfoList",  pageSize, pageNo);	// 사원목록 조회 Obj
 	MethodObj.setId("getTeamList");	// ID 셋팅  
	MethodObj.setParam("sEmpNo", sEmpNoPop.getValue());		// 사원코드
	MethodObj.setParam("sEmpName",		sEmpNamePop.getValue());		// 사원명
	MethodObj.setParam("sDepartName",	sDepartNamePop.getValue());	// 부서명
	MethodObj.setParam("sResignGubun",	sResignGubun.getValue());		// 퇴직여부
	MethodObj.setUrl("/common/CBSO033PController.do");  // 서버단 호출 URL  
 	MethodObj.MsgShow();							// PROGRASSBAR 표시 
	MethodObj.setMsg("조회 중");    		 		// 동기, 비동기시 PROGRASSBAR에 표시할 메시지 셋팅 
	MethodObj.setXPath("//*/ADMAB01");		// XPath
	MethodObj.addXPath("//*/vector");			// XPath
	MethodObj.addXPath("//*/WARNING");		// XPath
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]totalRows
	MethodObj.send();		// 최종 전송 
	//console.log("[/[/CBSO033P.xml] [doSearch()] ==> [조회 결과_51]" );

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_Search.setJSON(_tranSync.getDsJson("ADMAB01"));		// 조회 DS 셋팅(조회 결과) 
	Ds_TotCnt.setJSON(_tranSync.getDsJson("vector")); 				// 조회 전체 건수 DS 셋팅(조회 결과) 
 	Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  		// Warning DS 셋팅(조회 결과)  
	//console.log("[/[/CBSO033P.xml] [getCollTargetList()] ==> [DS_갯수_nCount]"+ Ds_TotCnt.getCellData(0, "totalRows") );
 	//console.log("[/[/CBSO033P.xml] [getCollTargetList()] ==> [DS_갯수]"+ Ds_Search.getRowCount() +"[Ds_Search]"+ WebSquare.xml.serialize(Ds_Search.getAllXML()) ); 

	if (Ds_Warning.getRowCount() != 0) {
        wprom.alert(Ds_Warning.getCellData(0, "msg"));
        return false;
    }
    if (Ds_Search.getRowCount() < 1) {
    	if (bOpenSearchFlag) {
            wprom.alert("존재하지 않는 사원입니다.");
            wprom.close("");
           // wconv.close("noneClose");
        }
        wprom.alert("해당 데이터가 존재하지 않습니다.");
        return;
    } 
    sResignGubun.setValue("Y");		// 퇴직여부(Y: 재직) 
};

//**************************************************************************************************
// lfn_getColumnValue: 퇴직 여부 칼럼 expression  처리 함수
//**************************************************************************************************
scwin.lfn_getColumnValue = function(data, formattedData, rowIndex, colIndex) { 

	var sRslt = ""; 

	if (Grd_List.getColumnID(colIndex) == "sResignDate") {  // Grid 칼럼이 "퇴직 여부" 이면
		var sResignDate = Ds_Search.getCellData(rowIndex, "sResignDate");   // 퇴직 여부  

		if (util.isNull(sResignDate)) {  // Grid 칼럼이 "퇴직 여부"가 null 이면 
		 	sRslt = "재직"; 
		} else {
			 sRslt = sResignDate;
		}
	}

	return sRslt;
};

//---------------------------------------------------------------------------
// function 명 : Grd_List_OnCellClick
// 내용        :  조회된 그리드에서 선택된 조직 메인으로 리턴
//---------------------------------------------------------------------------
scwin.Grd_List_OnCellClick = function(nRow, nCol) {
	// console.log("[/[/CBSO033P.xml] [Grd_List_OnCellClick()] ==> [TEST]" );  

    var ind = nRow;
    if (ind < 0) return;

    scwin.uf_getDataChoice(nRow);
}; 

//---------------------------------------------------------------------------
// function 명 : Grd_List_OnCellDblClick
// 내용        :  조회된 그리드에서 선택된 조직 메인으로 리턴
//---------------------------------------------------------------------------
scwin.Grd_List_OnCellDblClick = function(row, col) {
   var nRow = row;
    if (nRow < 0) return;

	scwin.uf_getDataChoice(nRow);
};

//---------------------------------------------------------------------------
// function 명 : uf_getDataChoice
// 내용        :  조회된 그리드에서 선택된 조직 메인으로 리턴(창닫기)
//---------------------------------------------------------------------------
scwin.uf_getDataChoice = function(nRow) {

	var sArg = "";

	sArg = Ds_Search.getCellData(nRow, "sEmpNo");					// 사원코드
	sArg += "^"+ Ds_Search.getCellData(nRow, "sEmpName");		// 사원명
	sArg += "^"+ args.tab;
	sArg += "^"+ Ds_Search.getCellData(nRow, "sDepartCode");	// 부서코드
	sArg += "^"+ Ds_Search.getCellData(nRow, "sDepartName");	// 부서명 
	sArg += "^"+ Ds_Search.getCellData(nRow, "sResignDate");		// 퇴직일 
	console.log("[/CBSO033P.xml] [uf_getDataChoice()] ==> [sArg]"+ sArg );

	wprom.close(sArg);     // 파라미터(사원ID, 사원명, Tab명) 
};

//---------------------------------------------------------------------------
// function 명 : Btn_Close_OnClick
// 내용        : 닫기버튼 Event처리
//---------------------------------------------------------------------------
scwin.Btn_Close_OnClick = function(obj) {
    wprom.close("");
};

//**************************************************************************************************
// 페이징 처리 - 페이지 콤보 박스(Paging 처리 시작)
//**************************************************************************************************
scwin.Cmb_Page_OnChanged = function(info) { 
    scwin.Search(info.newValue);
};

//**************************************************************************************************
// 페이징 처리 - 이전 페이지 리스트
//**************************************************************************************************
scwin.Btn_PreV_OnClick = function(obj) { 
	var ftVal = wconv.toNumber(Ds_Page.getCellData(0, "FILTERCD")) - 1;
    if (wconv.toNumber(maxFilterVal) < wconv.toNumber(ftVal)) {
        wprom.alert("조회의 처음입니다.");
        return;
    } else if (wconv.toNumber(ftVal) < 1) {
        wprom.alert("조회의 처음입니다.");
        return;
    }
    wconv.filter(Ds_Page, "FILTERCD=='" + ftVal + "'", true);
    Cmb_Page.setValue(Ds_Page.getCellData(0, "CODE"));
    scwin.Search(Cmb_Page.getValue());
};

//**************************************************************************************************
// 페이징 처리 - 이전 페이지
//**************************************************************************************************
scwin.Btn_Pre_OnClick = function(obj) { 
	var Idx = Cmb_Page.getValue();
    if (wconv.toNumber(Idx) - 1 < 1) {
        wprom.alert("조회의 처음입니다.");
        return;
    }
    scwin.Search(wconv.toString(wconv.toNumber(Idx) - 1));
    if (Ds_Page.getCellData(0, "CODE") == Idx) {
        wconv.filter(Ds_Page, "FILTERCD=='" + wconv.toString(wconv.toNumber(Ds_Page.getCellData(0, "FILTERCD")) - 1) + "'", true);
        Cmb_Page.setValue(Ds_Page.getCellData((Ds_Page.getRowCount() - 1), "CODE"));
    } else {
        Cmb_Page.setValue(wconv.toNumber(Idx) - 1);
	}
};

//**************************************************************************************************
// 페이징 처리 - 다음 페이지
//**************************************************************************************************
scwin.Btn_Next_OnClick = function(obj) {

	var Idx = Cmb_Page.getValue();

	if (wconv.toNumber(maxPage) < wconv.toNumber(Idx) + 1) {
		wprom.alert("더이상 자료가 존재하지 않습니다");
		return;
	}

	scwin.Search(wconv.toString(wconv.toNumber(Idx) + 1));

	if (Ds_Page.getCellData((Ds_Page.getRowCount() - 1), "CODE") == Idx) {
		wconv.filter(Ds_Page, "FILTERCD=='" + wconv.toString(wconv.toNumber(Ds_Page.getCellData((Ds_Page.getRowCount() - 1), "FILTERCD")) + 1) + "'", true);
	}
	Cmb_Page.setValue(wconv.toNumber(Idx) + 1); 
};

//**************************************************************************************************
// 페이징 처리 - 다음 페이지 리스트(Paging 처리 끝)
//**************************************************************************************************
scwin.Btn_NextV_OnClick = function(obj) { 
	var ftVal = wconv.toNumber(Ds_Page.getCellData((Ds_Page.getRowCount() - 1), "FILTERCD")) + 1;
    if (wconv.toNumber(maxFilterVal) < wconv.toNumber(ftVal)) {
        wprom.alert("더이상 자료가 존재하지 않습니다");
        return;
    }
    wconv.filter(Ds_Page, "FILTERCD=='" + ftVal + "'", true);
    Cmb_Page.setValue(Ds_Page.getCellData(0, "CODE"));
    scwin.Search(Cmb_Page.getValue());
};

//---------------------------------------------------------------------------
// function 명 : sEmpName_OnKeyDown
// 내용        :  사원명 Key Down(엔터) 이벤트
