var pageSize = "5";
var pageCount = "10";
var sPsnCorpFlag;
var sCdNameFlag;
var sSerchKey;
var fv_args;
//---------------------------------------------------------------------------
// function 명 : lfn_Cmd_PsnCorpFlag_OnChanged
// 내용        : 개인/법인/단체구분 콤보 선택 이벤트
//---------------------------------------------------------------------------
scwin.lfn_Cmd_PsnCorpFlag_OnChanged = function() {
    Edt_SerchCd.setValue("");
    Edt_SerchChr.setValue("");
    if (Cmd_PsnCorpFlag.getValue() == "1") {
        //개인
        //Cmb_CdNameFlag.Contents = "<Contents>\t\t\t\t\t<Record Code=\"Cd\" Data=\"주민번호\" default=\"true\"/>\t\t\t\t\t<Record Code=\"Name\" Data=\"고객명\"/>\t\t\t\t</Contents>";
        Cmb_CdNameFlag.setNodeSet( "data:Ds_Personal" , "name" , "code" );
        Cmb_CdNameFlag.setValue("Cd");
        Edt_SerchChr.hide();
        Edt_SerchCd.show();
        Edt_SerchCd.setDisplayFormat("######-#######");
        Edt_SerchCd.setPlaceholder( "______-_______");

    } else if (Cmd_PsnCorpFlag.getValue() == "2") {
        //법인
        //Cmb_CdNameFlag.Contents = "<Contents>\t\t\t\t\t<Record Code=\"Cd\" Data=\"사업자번호\" default=\"true\"/>\t\t\t\t\t<Record Code=\"Name\" Data=\"법인명\"/>\t\t\t\t</Contents>";
        Cmb_CdNameFlag.setNodeSet( "data:Ds_Co" , "name" , "code" );
        Cmb_CdNameFlag.setValue("Cd");
        Edt_SerchChr.hide();
        Edt_SerchCd.show();
        Edt_SerchCd.setDisplayFormat("###-##-#####");
        Edt_SerchCd.setPlaceholder( "___-__-_____");
    } else if (Cmd_PsnCorpFlag.getValue() == "3") {
        //단체
        //Cmb_CdNameFlag.Contents = "<Contents>\t\t\t\t\t<Record Code=\"Name\" Data=\"단체명\" default=\"true\"/>\t\t\t\t</Contents>";
        //Cmb_CdNameFlag.setValue("Name");
        //Edt_SerchChr.show();
        //Edt_SerchCd.hide();
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_Cmb_CdNameFlag_OnChanged
// 내용        : 코드/명 구분 콤보 선택 이벤트
//---------------------------------------------------------------------------
scwin.lfn_Cmb_CdNameFlag_OnChanged = function() {
    Edt_SerchCd.setValue("");
    Edt_SerchChr.setValue("");
    if (Cmb_CdNameFlag.getValue() == "Cd") {
        if (Cmd_PsnCorpFlag.getValue() == "1") {
            //개인
            Edt_SerchChr.hide();
            Edt_SerchCd.show();
            Edt_SerchCd.setDisplayFormat("######-#######");
	        Edt_SerchCd.setPlaceholder( "______-_______");
        } else {
            Edt_SerchChr.hide();
            Edt_SerchCd.show();
            Edt_SerchCd.setDisplayFormat("###-##-#####");
    	    Edt_SerchCd.setPlaceholder( "___-__-_____");
        }
    } else {
        Edt_SerchChr.show();
        Edt_SerchCd.hide();
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼  이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_Search = function(e) {
    scwin.lfn_getSelectList("1");

    // 페이징처리를 위해 전체 총데이터수를 구한다.
    gfn_MakePagingData(Ds_Page, DsWs_Cnt.getCellData(0, "nCount"), pageSize, pageCount);
    Cmb_Page.setValue("1");
};

//---------------------------------------------------------------------------
// function 명 : lfn_getSelectList
// 내용        : 리스트 조회
//---------------------------------------------------------------------------
scwin.lfn_getSelectList = function(pageNo) {
    wconv.clear(DsWs_List); //리스트 내용삭제
    /*
    SOBJ_List.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
    var MethodObj = gfn_CreateMethodObj(SOBJ_List, "com.inswave.longterm.cst.cin.task.CBA120Task", "selectList", pageSize, pageNo);
    gfn_SetParam(MethodObj, "sUserID", util.getGdsValue("gUserID"));
    //gfn_SetParam(MethodObj, "sDoer", gDoer);
    gfn_SetParam(MethodObj, "sDoer", wconv.trim(Edt_gDoer.getValue()));
    gfn_SetParam(MethodObj, "sPsnCorpFlag", wconv.trim(Cmd_PsnCorpFlag.getValue()));
    gfn_SetParam(MethodObj, "sCdNameFlag", wconv.trim(Cmb_CdNameFlag.getValue()));
    if (Cmb_CdNameFlag.getValue() == "Cd") {
        gfn_SetParam(MethodObj, "sSerchKey", wconv.trim(Edt_SerchCd.getValue()));
    } else {
        gfn_SetParam(MethodObj, "sSerchKey", wconv.trim(Edt_SerchChr.getValue()));
    }
    var result = gfn_CallService(SOBJ_List, DOBJ_List, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.cst.cin.task.CBA120Task", "selectList", pageSize, pageNo);
	MethodObj.setId("selectList");

    MethodObj.setParam( "sUserID", util.getGdsValue("gUserID"));
    MethodObj.setParam( "sDoer", wconv.trim(Edt_gDoer.getValue()));
    MethodObj.setParam( "sPsnCorpFlag", wconv.trim(Cmd_PsnCorpFlag.getValue()));
    MethodObj.setParam( "sCdNameFlag", wconv.trim(Cmb_CdNameFlag.getValue()));
    if (Cmb_CdNameFlag.getValue() == "Cd") {
        MethodObj.setParam( "sSerchKey", wconv.trim(Edt_SerchCd.getValue()));
    } else {
        MethodObj.setParam( "sSerchKey", wconv.trim(Edt_SerchChr.getValue()));
    }

	MethodObj.setUrl("/common/CBSO041PController.do");
	MethodObj.setXPath("//*/hashtable/data/vector/data/LTICA01");
	MethodObj.addXPath("//*/hashtable/data/LTICA01Count");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_List.setJSON (_tranSync.getDsJson("LTICA01")); // dataset 데이터 
	util.jSonDsCreate(_tranSync.getDsJson("LTICA01Count"), "DsWs_Cnt");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }
    if (DsWs_Cnt.getCellData(0, "nCount") == null || DsWs_Cnt.getCellData(0, "nCount") <= 0) {
        wprom.alert("조회건이 없습니다.");
    } else {
        Stc_TotalCnt.setValue(DsWs_Cnt.getCellData(0, "nCount"));
    }    
};

//**************************************************************************************************
// 페이징 처리 - 페이지 콤보 박스
//**************************************************************************************************
scwin.Cmb_Page_OnChanged = function(info) {
    scwin.lfn_getSelectList(info.newValue);
};

//**************************************************************************************************
// 페이징 처리 - 이전 페이지 리스트
//**************************************************************************************************
scwin.Btn_PreV_OnClick = function(obj) {
    var ftVal = wconv.toNumber(Ds_Page.getCellData(Ds_Page.getRowPosition(), "FILTERCD")) - 1;
    if (wconv.toNumber(maxFilterVal) < wconv.toNumber(ftVal)) {
        wprom.alert("조회의 처음입니다.");
        return;
    } else if (wconv.toNumber(ftVal) < 1) {
        wprom.alert("조회의 처음입니다.");
        return;
    }
    wconv.filter(Ds_Page, "FILTERCD=='" + ftVal + "'",true);
    Cmb_Page.setValue(Ds_Page.getCellData(0, "CODE"));
    scwin.lfn_getSelectList(Cmb_Page.getValue());
};

//**************************************************************************************************
// 페이징 처리 - 이전 페이지
//**************************************************************************************************
scwin.Btn_Pre_OnClick = function(obj) {
    var Idx = Cmb_Page.getValue();
    if (wconv.toNumber(Idx) - 1 < 1) {
        wprom.alert("조회의 처음입니다.");
        return;
    }
    scwin.lfn_getSelectList(wconv.toString(wconv.toNumber(Idx) - 1));
    if (Ds_Page.getCellData(0, "CODE") == Idx) {
        wconv.filter(Ds_Page, "FILTERCD=='" + wconv.toString(wconv.toNumber(Ds_Page.getCellData(0, "FILTERCD")) - 1) + "'",true);
        Cmb_Page.setValue(Ds_Page.getCellData(Ds_Page.getTotalRow(), "CODE"));
        /*수작업 변환 가이드 (getTotalRow)
            필터 적용이 되어있는 row의 건수는 getRowCount API를 사용합니다.*/
    } else
        Cmb_Page.setValue(wconv.toNumber(Idx) - 1);
};

//**************************************************************************************************
// 페이징 처리 - 다음 페이지
//**************************************************************************************************
scwin.Btn_Next_OnClick = function(obj) {
    var Idx = Cmb_Page.getValue();
    if (wconv.toNumber(maxPage) < wconv.toNumber(Idx) + 1) {
        wprom.alert("더이상 자료가 존재하지 않습니다");
        return;
    }
    scwin.lfn_getSelectList(wconv.toString(wconv.toNumber(Idx) + 1));
    if (Ds_Page.getCellData(Ds_Page.getTotalRow(), "CODE") == Idx
    ) {
        wconv.filter(Ds_Page, "FILTERCD=='" + wconv.toString(wconv.toNumber(Ds_Page.getCellData(Ds_Page.getTotalRow(), "FILTERCD")) + 1
        ) + "'",true);
    }
    Cmb_Page.setValue(wconv.toNumber(Idx) + 1);
};

//**************************************************************************************************
// 페이징 처리 - 다음 페이지 리스트
//**************************************************************************************************
scwin.Btn_NextV_OnClick = function(obj) {
    var ftVal = wconv.toNumber(Ds_Page.getCellData(Ds_Page.getRowPosition(), "FILTERCD")) + 1;
    if (wconv.toNumber(maxFilterVal) < wconv.toNumber(ftVal)) {
        wprom.alert("더이상 자료가 존재하지 않습니다");
        return;
    }
    wconv.filter(Ds_Page, "FILTERCD=='" + ftVal + "'",true);
    Cmb_Page.setValue(Ds_Page.getCellData(0, "CODE"));
    scwin.lfn_getSelectList(Cmb_Page.getValue());
};

//---------------------------------------------------------------------------
// function 명 : btn_close_OnClick
// 내용        : 닫기버튼 click event처리
//---------------------------------------------------------------------------
scwin.btn_close_OnClick = function(e) {
    wprom.close(false);
};

//---------------------------------------------------------------------------
// function 명 : lfn_OnLoadCompleted
// 내용        : 폼로드할때 처리할 내용 
//---------------------------------------------------------------------------
scwin.init = function() {
	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
	} catch(e) {}

    if (wconv.length(util.getGdsValue("gUserID")) <= 0) {
        util.setGdsValue("gUserID", "5100015");
        util.setGdsVAlue("gDoer", "5100015");
    }
    Edt_gDoer.setValue(util.getGdsValue("gDoer"));
    var SuperUser = nfn_checkAuthByID( "DsWs_Auth", util.getGdsValue("gUserID"), "22100");
    if (SuperUser == true) {
        Edt_gDoer.show();
        St_gDoer.show();
    } else {
        Edt_gDoer.hide();
        St_gDoer.hide();
    }

    if (wconv.length(fv_args.sPsnCorpFlag) > 0) {
        Cmd_PsnCorpFlag.setValue(0);
        Cmd_PsnCorpFlag.setValue(fv_args.sPsnCorpFlag);
        //scwin.lfn_Cmd_PsnCorpFlag_OnChanged();
    }
    if (wconv.length(fv_args.sCdNameFlag) > 0) {
        Cmb_CdNameFlag.setValue(0);
        Cmb_CdNameFlag.setValue(fv_args.sCdNameFlag);
        //scwin.lfn_Cmb_CdNameFlag_OnChanged();
    }

    if (wconv.length(fv_args.sSerchKey) > 0) {
        if (fv_args.sCdNameFlag == "Cd") {
            Edt_SerchCd.setValue(fv_args.sSerchKey);
        } else if (fv_args.sCdNameFlag == "Name") {
            Edt_SerchChr.setValue(fv_args.sSerchKey);
        }
        scwin.lfn_Search();
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_btn_ok_OnClick
// 내용        : 확인버튼 클릭 이벤트 
//---------------------------------------------------------------------------
scwin.lfn_btn_ok_OnClick = function(e) {
    var nRowPos = -1;
    for (var i = 0; i < DsWs_List.getRowCount(); i++) {
        if (DsWs_List.getCellData(i, "sCheck") == "1") {
            nRowPos = i;
            break;
        }
    }
    if (nRowPos == -1) {
        wprom.alert("원하는 항목을 선택하세요");
        return;
    }
    var sRgstNo = DsWs_List.getCellData(nRowPos, "sRgstNo");
    var sCstName = DsWs_List.getCellData(nRowPos, "sCstName");
    var sCstId = DsWs_List.getCellData(nRowPos, "sCstId");
    var sNewJobCd = DsWs_List.getCellData(nRowPos, "sNewJobCd");
    var sNewJobNm = DsWs_List.getCellData(nRowPos, "sNewJobNm");
    alert(sRgstNo + "^" + sCstName + "^" + sCstId + "^" + sNewJobCd + "^" + sNewJobNm);
    wprom.close(sRgstNo + "^" + sCstName + "^" + sCstId + "^" + sNewJobCd + "^" + sNewJobNm);
};

//---------------------------------------------------------------------------
// function 명 : lfn_Grd_List_OnCellClick
// 내용        :  조회화면 그리드 셀클릭 이벤트 
//---------------------------------------------------------------------------
scwin.lfn_Grd_List_OnCellClick = function( nRow, nCell) {
    for (var i = 0; i < DsWs_List.getRowCount(); i++) {
        DsWs_List.setCellData(i, "sCheck", "0");
    }
    DsWs_List.setCellData(nRow, "sCheck", "1");
};

//---------------------------------------------------------------------------
// function 명 : lfn_Grd_List_OnCellDblClick
// 내용        :  조회화면 그리드 셀더블클릭 이벤트 
//---------------------------------------------------------------------------
scwin.lfn_Grd_List_OnCellDblClick = function(nRow, nCell) {
    //scwin.lfn_Grd_List_OnCellClick( nRow, nCell);
    scwin.lfn_btn_ok_OnClick();
};

