scwin.init = function() {

	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
	} catch(e) {}

    var resultArr = fv_args.jobargs.split( "^" );

    Edt_JobGroup.setValue(wconv.trim(resultArr[0]));
    Edt_JobCode.setValue(wconv.trim(resultArr[1]));
    //cd_gubn1.setSelectedIndex(0);
    if (wconv.length(wconv.trim(resultArr[2])) > 0) {
        gbn = wconv.trim(resultArr[2]);
    }
    if (wconv.length(wconv.trim(resultArr[3])) > 0) {
        sJobGroup = wconv.trim(resultArr[3]);
    }
    if (wconv.length(wconv.trim(resultArr[4])) > 0) {
        sJobCode = wconv.trim(resultArr[4]);
    }
    if (wconv.length(wconv.trim(resultArr[5])) > 0) {
        sTeachType = wconv.trim(resultArr[5]);
    }

    //gbn, sJobGroup, sJobCode, sTeachType
    gfn_getCommonCodes("DsWs_Area", "Area", "cd_gubn1", "A");
    //wconv.trace("group" + Edt_JobGroup);
    //wconv.trace("code" + Edt_JobCode);
};

