//기관 주소 
scwin.bt_gigwan_OnClick = function(obj) { 
    var promise = Promise.resolve();
    var args = "";
    promise = promise.then(function(rtnVal) {
        //"Ds_AddrHome^0^sZip1^sZip2^sAdrs^sAdrsAdd^sAdrs1^sAdrs2^sAdrs3";
        return wprom.promise.popupOpen("PROJECT_PATH_TEMP/com/fCom01060s.xml", "parentInfo=" + args /*wprom.popupOpen(가이드참조)*/ , 100, 100, true, 300, 100);
    });
    promise = promise.then(function(ret) {
        var retArr = wconv.split(ret, "|");
        /*
    retArr[0] = 우편번호 앞 3 자리
    retArr[1] = 우편번호 뒤 3 자리
    retArr[2] = 주소 ex(서울 종로구 번1동)	
    retArr[3] = 빌딩명
    retArr[4] = 번지
    retArr[5] = ex(서울)
    retArr[6] = ex(종로구)
    retArr[7] = ex(번1동)
    */
        Edt_1_Zip1.setValue(wconv.trim(retArr[0]));
        Edt_1_Zip2.setValue(wconv.trim(retArr[1]));
        Edt_1_sAdrs.setValue(wconv.trim(retArr[2]));
        Edt_1_AdrsAdd.setValue(wconv.trim(retArr[4] + " " + retArr[3]));
    });
    return promise;
};

//  데이터를 받아서 닫는다. 
scwin.bt_recive_OnClick = function(obj) {
    var sRecSchoolName = scwin.lfn_null2space(ED_sSchoolName.getValue());
    var sRecZip1 = scwin.lfn_null2space(Edt_1_Zip1.getValue());
    var sRecZip2 = scwin.lfn_null2space(Edt_1_Zip2.getValue());
    var sRecAdrs1 = scwin.lfn_null2space(Edt_1_sAdrs1);
    var sRecAdrs2 = scwin.lfn_null2space(Edt_1_sAdrs2);
    var sRecAdrs3 = scwin.lfn_null2space(Edt_1_sAdrs3);
    var sRecAdrsAdd = scwin.lfn_null2space(Edt_1_AdrsAdd.getValue());
    var ret = wconv.trim(sSchoolCode) + "^" + // 0 : 학교코드 
        sRecSchoolName + "^" + // 1 : 학교명 
        sRecZip1 + "^" + // 2 : 우편번호1
        sRecZip2 + "^" + // 3 : 우편번호2
        sRecAdrs1 + "^" + // 4 : 주소 1
        sRecAdrs2 + "^" + // 5 : 주소 2
        sRecAdrs3 + "^" + // 6 : 주소 3
        sRecAdrsAdd + "^" + // 7 : 나머지 주소 
        wconv.trim(cd_gubn.getValue()) + "^" + // 8 : 회원구분  
        //2014.2.6 새주소
        DS_fCut01015.getCellData(DS_fCut01015.getRowPosition(), "sZipType") + "^" + // 9 : 우편번호유형
        DS_fCut01015.getCellData(DS_fCut01015.getRowPosition(), "sAddrMgtNo") + "^" + // 10 : 주소관리번호
        DS_fCut01015.getCellData(DS_fCut01015.getRowPosition(), "nAddrStdSeq") + "^" + // 11 : 주소표준순번 
        DS_fCut01015.getCellData(DS_fCut01015.getRowPosition(), "sOrgZipType") + "^" + // 12 : 원본주소구분
        DS_fCut01015.getCellData(DS_fCut01015.getRowPosition(), "sOrgZipCode") + "^" + // 13 : 원본우편번호 
        DS_fCut01015.getCellData(DS_fCut01015.getRowPosition(), "sOrgAddr"); // 14 : 원본주소구분 
console.log("★★★★★★★★★★★★★★★ret 직업정보 관리 창 닫을때 " + ret);
    wprom.close(ret);
};

scwin.ED_sSchoolName_OnKeyDown = function(e) {
    // Enter 를 입력한 경우
    if (e.keyCode == 13) {
        scwin.bt_search_OnClick();
    }
};

scwin.div_grid_Gd_job_OnCellClick = function( nRow, nCell) {
    var sSchoolDiv = DS_fCut01015.getCellData(nRow, "sSchoolDiv");
    if (gbn == "POP" || (gbn == "1" && sTeachRel == "1001")) {
        if (!isMatchJob(sJobGroup, sJobCode, sSchoolDiv)) {
            return;
        } else {
            sSchoolCode = wconv.trim(DS_fCut01015.getCellData(nRow, "sSchoolCode"));
            ED_sSchoolName.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sSchoolName")));
            Edt_1_Zip1.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sZip1")));
            Edt_1_Zip2.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sZip2")));
            Edt_1_sAdrs.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs1")) + " " + wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs2")) + " " + wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs3")));
            Edt_1_sAdrs1 = wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs1"));
            Edt_1_sAdrs2 = wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs2"));
            Edt_1_sAdrs3 = wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs3"));
            Edt_1_AdrsAdd.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrsAdd")));
        }
    } else {
        sSchoolCode = wconv.trim(DS_fCut01015.getCellData(nRow, "sSchoolCode"));
        ED_sSchoolName.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sSchoolName")));
        Edt_1_Zip1.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sZip1")));
        Edt_1_Zip2.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sZip2")));
        Edt_1_sAdrs.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs1")) + " " + wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs2")) + " " + wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs3")));
        Edt_1_sAdrs1 = wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs1"));
        Edt_1_sAdrs2 = wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs2"));
        Edt_1_sAdrs3 = wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrs3"));
        Edt_1_AdrsAdd.setValue(wconv.trim(DS_fCut01015.getCellData(nRow, "sAdrsAdd")));
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_null2space
// 내용        :  null이거나 공백일때 한자리수 공백으로 채워서 리턴
//---------------------------------------------------------------------------
scwin.lfn_null2space = function(str) {
    if (wconv.length(wconv.trim(str)) == 0) {
        return " ";
    } else {
        return str;
    }
};

// 코드 선택 (공제회원 여부 확인)
scwin.lfn_gubn_OnChanged = function(obj, strCode, strText, nOldIndex, nNewIndex) {
    if (!isTeachJob4(sJobGroup, sJobCode) && sJobCode != "0308") {
        if (sTeachType == "1" || sTeachType == "2") { 
            wprom.alert("해당직업은  공제회원 여부를 선택할 수 없습니다");
            cd_gubn.setValue("N");
            cd_gubn.focus();
            return;
        }
    }
};
