var pageSize = "";
var pageNo = "";

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : CRS 본인정보 조회
//---------------------------------------------------------------------------
scwin.lfn_Search_Async = function() { 
    util.eventAsyncCall(this, scwin.lfn_Search, arguments);
};

scwin.lfn_Search = function() {
    //데이터셋 초기화
    wconv.clear(DsWs_Warning);
    //DsWs_Warning.DataObject = "DOBJ_LTIDA65";
    //var MethodObj;
    wconv.clear(DsWs_LTIEA65);
    wconv.clear(DsWs_LTIEA00);
    wconv.clear(DsWs_LTIDA65);
    wconv.clear(DsWs_LTIDA00);
    wconv.clear(DsWs_LTIDA03);

    /*SOBJ_LTIDA65.WSDL = gds.gWsdlBasis; //기본웹서비스
    MethodObj = gfn_CreateMethodObj(SOBJ_LTIDA65, "com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida65", "", "");
    gfn_SetParam(MethodObj, "sPlanNo", wconv.trim(sPlanNo)); // 설계/계약 번호
    gfn_SetParam(MethodObj, "sGb", wconv.trim(sGb));
    gfn_CallService(SOBJ_LTIDA65, DOBJ_LTIDA65, MethodObj);*/

   	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida65", pageSize, pageNo);	
	MethodObj.setId("getLtida65");
	MethodObj.setParam( "sPlanNo", wconv.trim(sPlanNo)); // 설계/계약 번호
	MethodObj.setParam( "sGb", wconv.trim(sGb));

	MethodObj.setUrl("/common/CBSO048PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA65DocRtn']/LTIDA65");//(dsWS의 XPath)
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA03DocRtn']/LTIDA03");//(dsWS의 XPath)
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA03DocRtn']/LTIDA00");//(dsWS의 XPath)
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA00DocRtn']/LTIEA00");//(dsWS의 XPath)
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA65DocRtn']/LTIEA65");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA65"), "DsWs_LTIDA65");//Json에 자동적으로 생성되게 해줌
	util.jSonDsCreate(_tranSync.getDsJson("LTIDA03"), "DsWs_LTIDA03");//Json에 자동적으로 생성되게 해줌
	util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_LTIDA00");//Json에 자동적으로 생성되게 해줌
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LTIEA00");//Json에 자동적으로 생성되게 해줌
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA65"), "DsWs_LTIEA65");//Json에 자동적으로 생성되게 해줌

	util.setCompBindRefresh("DsWs_LTIDA65");//바인딩을 해줌	
	util.setCompBindRefresh("DsWs_LTIDA03");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_LTIDA00");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_LTIEA00");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_LTIEA65");//바인딩을 해줌

	util.setRowPosition(DsWs_LTIDA65, 0);	
	util.setRowPosition(DsWs_LTIDA03, 0);	
	util.setRowPosition(DsWs_LTIDA00, 0);	
	util.setRowPosition(DsWs_LTIEA00, 0);	
	util.setRowPosition(DsWs_LTIEA65, 0);	

    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
    if (DsWs_Warning.getRowCount() != 0) {
          wprom.alert(DsWs_Warning.getCellData(0, "msg"));
          return;
    }
    if (DsWs_LTIDA65.getCellData(0, "result") > 0) {
        util.copyM(Ds_LTIDA65, DsWs_LTIDA65.getAllJSON());
		util.setCompBindRefresh(Ds_LTIDA65.getID());
    	util.setRowPosition(Ds_LTIDA65, 0);	
//        wconv.copy(Ds_LTIDA65, DsWs_LTIDA65);

    } else if (DsWs_LTIEA65.getCellData(0, "result") > 0) {
    	util.copyM(Ds_LTIDA65, DsWs_LTIEA65.getAllJSON());
		util.setCompBindRefresh(Ds_LTIDA65.getID());
    	util.setRowPosition(Ds_LTIDA65, 0);	

//        wconv.copy(Ds_LTIDA65, DsWs_LTIEA65);        
    } else {
        Ds_LTIDA65.undoAll();
        util.appendRow(Ds_LTIDA65);
    }
    edt_sTelNo.setValue(util.nvl(DsWs_LTIDA03.getCellData(0, "sCellPhone1"),"") + "-" + util.nvl(DsWs_LTIDA03.getCellData(0, "sCellPhone2"),"") + "-" + util.nvl(DsWs_LTIDA03.getCellData(0, "sCellPhone3"),""));
    edt_sAddress.setValue("(" + util.nvl(DsWs_LTIDA03.getCellData(0, "sZip1"),"") + "-" + util.nvl(DsWs_LTIDA03.getCellData(0, "sZip2"),"") + ")" + util.nvl(DsWs_LTIDA03.getCellData(0, "sAdrs1"),"") + "  " + util.nvl(DsWs_LTIDA03.getCellData(0, "sAdrsAdd"),""));
    scwin.rdo_Qust1_Onradioclick();
};

