scwin.lfn_Save = function() {

    wconv.clear(DsWs_Warning);    
    //DsWs_Warning.DataObject = "DOBJ_Save";
    if (scwin.lfn_Validation_Check()) {
        Ds_LTIDA65.setCellData(0, "sPlanNo", sPlanNo);
        Ds_LTIDA65.setCellData(0, "sInptDler", util.getGdsValue("gUserID"));//gds.gUserID);
        Ds_LTIDA65.setCellData(0, "sMdfcDler", util.getGdsValue("gUserID"));//gds.gUserID);
        Ds_LTIDA65.setCellData(0, "sInptDlerIP", util.getGdsValue("gUserIP"));//gds.gUserIP);
        Ds_LTIDA65.setCellData(0, "sMdfcDlerIP", util.getGdsValue("gUserIP"));//gds.gUserIP);
       // SOBJ_save.WSDL = gds.gWsdlBasis;

        /*var MethodObj = SOBJ_save.CreateMethodObj("perform");
        setHeader("hashtable", "com.inswave.longterm.ssr.pln.task.DBB013Task", "saveLtida65");
        addSingleData("LTIDA65", "LTIDA65", Ds_LTIDA65);
        setTail("hashtable");
        SOBJ_save.CreateObj("in0");
        MethodObj.in0 = insertStr;
        gfn_CallService(SOBJ_save, DOBJ_save, MethodObj);*/

        var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "saveLtida65");
 		MethodObj.setId("saveLtida65"); 
 		MethodObj.addSingleData("LTIDA65", "LTIDA65", Ds_LTIDA65.getID());
 		MethodObj.setUrl("/common/CBSO046PController.do");   // 서버단 호출 URL (수납 확정)    
 		MethodObj.setXPath("//*/WARNING"); // XPath
 		MethodObj.setHashTable();    //  전문의 앞이 hashtable로 보낼 때
 		MethodObj.sync();     // 동기 처리[기본(async:비동기)]
 		MethodObj.send();      // 최종 전송   // TEST @@@ ===>
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
        //에러처리
        if (DsWs_Warning.getRowCount() != 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
            return;
        }
        wprom.alert("CRS 본인정보 사항이 확인 되었습니다.");
        wprom.close(); //lfn_Search(obj);
    }
};

