//---------------------------------------------------------------------------
// function 명 : lfn_setComponent
// 내용        : 수정여부 세팅
//---------------------------------------------------------------------------
scwin.lfn_setComponent = function(val) {
    if (val == false) {
        if (sFatcaFlgCd == "01") {
            edt_sSurname.setValue("");
            edt_sGivenName.setValue("");
            edt_sAddr.setValue("");
            msk_sItinSsnNo.setValue("");
            msk_sItinSsnNo2.setValue("");
            msk_sItinSsnNo3.setValue("");
            edt_sRsdNatnl.setValue("");
            edt_sRsdNatnl2.setValue("");
            edt_sRsdNatnl3.setValue("");
            rdo_NinptResnCd.setValue("");
            rdo_NinptResnCd2.setValue("");
            rdo_NinptResnCd3.setValue("");
            edt_NinptResnCd.setValue("");
            chk_sFatcaY.setValue("");
        }
        edt_sSurname.setDisabled(false);
        edt_sGivenName.setDisabled(false);
        edt_sAddrv.setDisabled(false);
        msk_sItinSsnNo.setDisabled(true);
        msk_sItinSsnNo2.setDisabled(true);
        msk_sItinSsnNo3.setDisabled(true);
        edt_sRsdNatnl.setDisabled(true);
        edt_sRsdNatnl2.setDisabled(true);
        edt_sRsdNatnl3.setDisabled(true);
        rdo_NinptResnCd.setDisabled(true);
        rdo_NinptResnCd2.setDisabled(true);
        rdo_NinptResnCd3.setDisabled(true);
        edt_NinptResnCd.setDisabled(true);
        chk_sFatcaFlgCd_02.setDisabled(false);
        chk_sFatcaFlgCd_03.setDisabled(false);
        chk_sFatcaFlgCd_04.setDisabled(false);
        chk_sFatcaFlgCd_05.setDisabled(false);
    } else {
        if (sFatcaFlgCd != "01") {
            //chk_sFatcaY.Value = "1";
            chk_sFatcaY.setValue(true);
        }
        edt_sSurname.setDisabled(true);
        edt_sGivenName.setDisabled(true);
        edt_sAddr.setDisabled(true);
        msk_sItinSsnNo.setDisabled(false);
        msk_sItinSsnNo2.setDisabled(false);
        msk_sItinSsnNo3.setDisabled(false);
        edt_sRsdNatnl.setDisabled(false);
        edt_sRsdNatnl2.setDisabled(false);
        edt_sRsdNatnl3.setDisabled(false);
        rdo_NinptResnCd.setDisabled(false);
        rdo_NinptResnCd2.setDisabled(false);
        rdo_NinptResnCd3.setDisabled(false);
        edt_NinptResnCd.setDisabled(false);
        chk_sFatcaFlgCd_02.setDisabled(true);
        chk_sFatcaFlgCd_03.setDisabled(true);
        chk_sFatcaFlgCd_04.setDisabled(true);
        chk_sFatcaFlgCd_05.setDisabled(true);
        edt_sSurname.SetFocus();
    }
};


