//---------------------------------------------------------------------------
// function 명 : msk_sItinSsnNo_OnChanged
// 내용        : 납세자번호 변경시
//---------------------------------------------------------------------------
scwin.msk_sItinSsnNo_OnviewChanged = function(info) {
    if (this.id != "") {//strText != ""    	
        if (this.id == "msk_sItinSsnNo") {
            rdo_NinptResnCd.setValue("");
            rdo_NinptResnCd.setDisabled(true);
        } else if (this.id == "msk_sItinSsnNo2") {
            rdo_NinptResnCd2.setValue("");
            rdo_NinptResnCd2.setDisabled(true);
        } else if (this.id == "msk_sItinSsnNo3") {
            rdo_NinptResnCd3.setValue("");
            rdo_NinptResnCd3.setDisabled(true);
        }
        edt_NinptResnCd.setValue("");
    } else {
        if (this.id == "msk_sItinSsnNo") {
            rdo_NinptResnCd.setValue("");
            rdo_NinptResnCd.setDisabled(false);
        } else if (this.id == "msk_sItinSsnNo2") {
            rdo_NinptResnCd2.setValue("");
            rdo_NinptResnCd2.setDisabled(false);
        } else if (this.id == "msk_sItinSsnNo3") {
            rdo_NinptResnCd3.setValue("");
            rdo_NinptResnCd3.setDisabled(false);
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : msk_sItinSsnNo1_OnChanged
// 내용        : 납세자번호 변경시
//---------------------------------------------------------------------------
scwin.msk_sItinSsnNo1_OnviewChanged = function(info) {
    if (this.id != "") {//strText != ""
        if (this.id == "msk_RN1") {
            rdo_RN1.setValue("");
            rdo_RN1.setDisabled(true);
        } else if (this.id == "msk_RN2") {
            rdo_RN2.setValue("");
            rdo_RN2.setDisabled(true);
        }
        edt_RNResn.setValue("");
    } else {
        if (this.id == "msk_RN1") {
            rdo_RN1.setValue("");
            rdo_RN1.setDisabled(false);
        } else if (this.id == "msk_RN2") {
            rdo_RN2.setValue("");
            rdo_RN2.setDisabled(false);
        }
    }
};

