//------------------------------------------------------------------------------

scwin.rdo_Qust1_Onradioclick = function(index, value) {

    if (Ds_LTIDA65.getCellData(0, "sQust1") == "Y") { 	
        rdo_Qust1.setDisabled(false);
        rdo_Qust2.setDisabled(true);
        rdo_Qust2.setValue("");
        rdo_Qust5.setDisabled(false);
    } else if (Ds_LTIDA65.getCellData(0, "sQust1") == "N") {
        rdo_Qust5.setDisabled(true);
        rdo_Qust5.setValue("");
        rdo_Qust2.setDisabled(false);
    } else {
        rdo_Qust2.setDisabled(true);
        rdo_Qust2.setValue("");
        rdo_Qust5.setDisabled(true);
        rdo_Qust5.setValue("");
    }
    scwin.rdo_Qust2_Onradioclick();
    scwin.rdo_Qust5_Onradioclick();
};
