head.script:

 /**
  * 초기화 함수(셀렉트 박스의 항목을 가져온다)
  */
 scwin.init = function() {
  console.log("[/PD01P01.xml] [init()] ==> [TEST_01] [조성 Data Impor 화면_초기화]");

  scwin.parentObj = opener || parent;
  scwin.popupID = $w.getParameter("popupID");
  scwin.messageObj = $w.getParameter("messageObj");   

  dma_Search.setJSON(scwin.messageObj.VdltMaster); 
  //console.log("[/PD01P01.xml] [init()] ==> [TEST_06] [AREA_CODE]"+ dma_Search.get("AREA_CODE") +"[LINE_CODE]"+ dma_Search.get("LINE_CODE") );

  dlt_GlassList.setJSON(scwin.messageObj.VbaseCount);

  scwin.Search_onclick(); // 자동 조회 
 };

 /**
  * 01. 조회
  */
 scwin.Search_onclick = function() {
  console.log("[/PD01P01.xml] [Search_onclick()] ==>  [TEST_01] [조회]" );

  dma_Search.set("AREA_CODE", dma_Search.get("AREA_CODE"));  // 사업장 코드
  dma_Search.set("LINE_CODE", dma_Search.get("LINE_CODE"));  // LINE_CODE(탱크 코드)
  dma_Search.set("START_DATE", dma_Search.get("START_DATE"));
  dma_Search.set("END_DATE", dma_Search.get("END_DATE"));
//  console.log("[/PD01P01.xml] [Search_onclick()] ==> [TEST_06] [AREA_CODE]"+ dma_Search.get("AREA_CODE") +"[LINE_CODE]"+ dma_Search.get("LINE_CODE") 
//   +"[START_DATE]"+ dma_Search.get("START_DATE") +"[END_DATE]"+ dma_Search.get("END_DATE") );

  common.executeSubmission("sbm_getSelectPd01Search");    // target='data:json,["dma_BaseMap","dlt_GlassList"]' ==> dlt Ds 생성됨
 };

 /**
  * 그리드 헤더부분 조회 후 데이터셋 동적 셋팅 함수 호츌
  */
 scwin.sbm_getSelectPd01Search_submitdone = function(e) {
  console.log("[/PD01P01.xml] [sbm_getSelectPd01Search_submitdone()] ==> [TEST_02 [ dlt_GlassList.getRowCount()]"+  dlt_GlassList.getRowCount() );

  scwin.setColumnID();  // DataList & Grid 동적 Column 생성
 };

 //DataList & Grid 동적 Column 생성
 scwin.setColumnID = function() {
  console.log("[/PD01P01.xml] [setColumnID()] ==> [TEST_01]");

  var column = {};
  var columnArr = [];
  var gridHeader ="", gridBody="";
  var GlassCount = dlt_GlassList.getRowCount();
  var glass_code = dma_BaseMap.get("GLASS_CODE");
  console.log("[/PD01P01.xml] [setColumnID()] ==> [TEST_03] [GlassCount]"+ GlassCount );

  //주의사항
  //1. 각 DataList, GridView 생성에 사용될 xml tag (<w2:dataList>,  <w2:columnInfo>, <w2:column> 등)들은 현재 "Source"탭에 그려져 있는 그대로를 사용해야 함
  //2. <w2:gridView> 생성 시 아래 속성을 필히! 추가하여야 함
  //   <w2:gridView xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms"

  //1. DataList 생성 시작 =============================================================================
  //1.1. DataList 고정 Column 생성 
    var dataListStr1 = '<w2:dataList baseNode="list" id="dlt_ProGdList" repeatNode="map" saveRemovedData="true" style="">'
  + '         <w2:columnInfo>'
        + '       <w2:column dataType="text" id="LINE_CODE" name=""></w2:column>'
        + '       <w2:column dataType="text" id="INSPECT_DATE" name=""></w2:column>'
        + '       <w2:column dataType="text" id="INSPECT_DATE2" name=""></w2:column>'
        + '       <w2:column dataType="text" id="INSPECT_TIME" name=""></w2:column>'
        + '       <w2:column dataType="text" id="AREA_CODE" name=""></w2:column>'
        + '       <w2:column dataType="text" id="PROGD_SQL" name=""></w2:column>'
        + '       <w2:column dataType="text" id="TOTAL" name=""></w2:column>'
        + '       <w2:column dataType="text" id="START_DATE" name=""></w2:column>'
     + '       <w2:column dataType="text" id="ETC" name=""></w2:column>'
        + '       <w2:column dataType="text" id="SUB_ETC" name=""></w2:column>'
        + '       <w2:column dataType="text" id="ETC_FLGE" name=""></w2:column>'
  console.log("[/PD01P01.xml] [setColumnID()] ==> [TEST_04] [GlassCount]"+ GlassCount );

  for( var g=1; g<=GlassCount+1; g++ ) {       // dlt_GlassList Ds를 루프를 돌면서 dlt_ProGdList Ds에 추가를 시킴 ==> 동적 생성 @@@
   var T = "T"+ g;
   //console.log("[/PD01P01.xml] [setColumnID()] ==> [TEST_51] [g_번째]"+ g +"[T]"+ T );
   dataListStr1 += '  <w2:column dataType="text" id="'+T+'" name=""></w2:column>'
  }
  //console.log("[/PD01P01.xml] [setColumnID()] ==> [TEST_61] [dataListStr1]"+ dataListStr1 );

  //1.4. DataList 모든 Column 생성 마무리
        dataListStr1 += '  </w2:columnInfo>'
        + '      <w2:data use="false"></w2:data>'
        + '       </w2:dataList>';

  scwin.makeDataList( "dlt_ProGdList", dataListStr1 );  // 2. 날자별 시료 정보 조회 Dtl
  //console.log("[/PD01P01.xml] [setColumnID()] ==> [TEST_71] [dataListStr1]"+ dataListStr1 );

  scwin.makeGrid();  // DataList & Grid 동적 Column 생성
 };

 /**
  * 동적 DataList 객체 생성
  */
 scwin.makeDataList = function(id, xmlStr) {  
  console.log("[/PD01P01.xml] [makeDataList()] ==> [TEST_01]");

  //동일 DataList 존재시 삭제
  if( !wconv.isNull( WebSquare.util.getComponentById( id ) ) ) {
   $w.data.remove( id );
  }

  $w.data.create( xmlStr );
 };

 /**
  *  77.  Grid 다시 생성(동적 Grid)
 */    
 scwin.makeGrid = function() {
  console.log("[/PD01P01.xml] [makeGrid()] ==> [TEST_01]" );

  var column = {};
  var columnArr = [];
  var gridHeader ="", gridBody="";
  var GlassCount = dlt_GlassList.getRowCount();
  var glass_code = dma_BaseMap.get("GLASS_CODE");   // Glass 코드
  console.log("[/PD01P01.xml] [makeGrid()] ==> [TEST_05] [glass_code]"+ glass_code );

  // 2. 날자별 시료 정보 Grid 생성 시작 =============================================================================
        // 2. 날자별 시료 정보 Grid 생성 
        //     : xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
       var gridStr = '<w2:gridView id="grd_list" xmlns="http://www.w3.org/1999/xhtml" xmlns:ev="http://www.w3.org/2001/xml-events" xmlns:w2="http://www.inswave.com/websquare" xmlns:xf="http://www.w3.org/2002/xforms" '
        + '               dataList="data:dlt_ProGdList" style="height:185px;" width="100" autoFit="" class="cm_grid" defaultCellHeight="30" ev:onafteredit="" ev:oncellclick="scwin.grd_list_oncellclick" ev:onrowindexchange="" fixedColumnWithHidden="true" ignoreCellClick="false" overflowX="auto" overflowY="auto" readOnly="true" rowStatusHeaderValue="" rowStatusVisible="" scrollByColumn="false" showSortableImage="" sortEvent="onclick" sortable="true" useShiftKey="true" visibleRowNum="">'
        + ' <w2:header id="header1" style="">'
        // 1-2. 날자별 시료 정보 Grid 오른쪽 Header 칼럼명(glass_code: 첫번? 칼럼, Time 칼럼) 생성
       + '<w2:row id="row1" style="">'
        + '   <w2:column blockSelect="false" id="REQ_NUMBER" removeBorderStyle="false" style="height:25px;" width="100" inputType="text" value="LINE_CODE" displayMode="label" colSpan="" rowSpan="1" sortLabel="" sortable=""></w2:column>'   // LINE_CODE
     + '   <w2:column blockSelect="false" id="REQ_DATE" removeBorderStyle="false" style="height:25px;" width="100" inputType="text" value="INSPECT_DATE" displayMode="label" colSpan="" rowSpan="1" sortLabel="" sortable=""></w2:column>'   // INSPECT_DATE
   + '   <w2:column blockSelect="false" id="INSPECT_TIME" removeBorderStyle="false" style="height:25px;" width="100" inputType="text" value="INSPECT_TIME" displayMode="label" colSpan="" rowSpan="1" sortLabel="" sortable=""></w2:column>'   // INSPECT_TIME
    // + '   <w2:column blockSelect="false" id="AREA_CODE" removeBorderStyle="false" style="height:25px;" width="100" inputType="text" value="AREA_CODE" displayMode="label" colSpan="" rowSpan="1" sortLabel="" sortable=""></w2:column>'   // AREA_CODE

  // 1-2. 주제별 시료 정보 Grid 오른쪽 Header 칼럼명 생성(왼쪽부터 차례로 시료 정보 데이타 넣기))[SiO2, Al2O3, B2O3 ....]
  for( var r=0; r<=GlassCount-1; r++ ) {
   var headame = dlt_GlassList.getCellData( r, "ITEM_CODE");
   gridStr += '<w2:column blockSelect="false" id="" removeBorderStyle="false" style="height:25px;" width="100" inputType="text" value="'+headame+'" displayMode="label" colSpan="" rowSpan="1"></w2:column>';
  }

  // 1-3. 주제별 시료 정보 Grid 오른쪽 Header 칼럼명(TOTAL 칼럼, 특이사항 칼럼) 생성
     gridStr += '  <w2:column blockSelect="false" id="" removeBorderStyle="false" style="height:25px;" inputType="text" width="100" value="TOTAL" displayMode="label" sortable=""></w2:column>'
     + '</w2:row>'
        + ' </w2:header>'

        // 2. 날자별 시료 정보 Grid Body 생성 시작 ===============================================================================
        // 1-4. 주제별 시료 정보 Grid Body 생성(DATE, T1)
        + '<w2:gBody id="gBody1" style="">'
        + '<w2:row id="row1" style="">'
     + '    <w2:column blockSelect="false" id="LINE_CODE" width="100" value="" removeBorderStyle="false" style="" inputType="text" displayMode="label"></w2:column>'    // LINE_CODE
     + '    <w2:column blockSelect="false" id="INSPECT_DATE" width="100" removeBorderStyle="false" footerDiv="false" inputType="text" displayMode="label" disabled="true" textAlign="right"></w2:column>'  // INSPECT_DATE
     + '    <w2:column blockSelect="false" id="INSPECT_TIME" width="100" removeBorderStyle="false" footerDiv="false" inputType="text" displayMode="label" disabled="true" textAlign="right"></w2:column>'  // INSPECT_TIME
     //   + '    <w2:column blockSelect="false" id="AREA_CODE" width="100" removeBorderStyle="false" footerDiv="false" inputType="text" displayMode="label" disabled="true" textAlign="right"></w2:column>'  // AREA_CODE

       // 1-4. 주제별 시료 정보 Grid Body 데이타 생성(왼쪽부터 차례로 수치 데이타 넣기)
  for( var r=2; r<=GlassCount+1; r++ ) { // dlt_GlassList Ds를 루프를 돌면서 dlt_ProGdList Ds에 추가를 시킴 ==> 동적 생성 @@@
     var T1 = "T"  + wconv.toNumber(r);
   gridStr += ' <w2:column blockSelect="false" id="'+T1+'" removeBorderStyle="false" style="" inputType="text" width="100" value="" dataType="number" displayFormat="##.##0" allowChar="0-9." maxlength="7" displayMode="label" textAlign="right"></w2:column>'; 
  } // end of for()

  // 1-5. 주제별 시료 정보 Grid Body 데이타 칼럼(마지막 칼럼) 생성
     gridStr += '  <w2:column blockSelect="false" id="SAMPLE_NO" removeBorderStyle="false" style="" inputType="text" width="100" value="" displayMode="label"> </w2:column>'
        + '</w2:row>'
        + '</w2:gBody>'
        + '</w2:gridView>'; 
      grd_list.setCellBackgroundColor( 1, 2 ,"#FFFF00" );    // 배경색 셋팅(상위 Grid row에 배경색 설정)
    console.log("[/PD01P01.xml] [makeGrid()] ==> [TEST_51] [gridStr]"+ gridStr );

  grd_list.setGridStyle( WebSquare.xml.parse( gridStr , true ) );   // 2. 날자별 시료 정보 조회

  var nRow = dlt_ProGdList.insertRow(0);

  dlt_ProGdList.setCellData( nRow , "INSPECT_DATE2" , "");   // 측정일시
  console.log("[/PD01P01.xml] [makeGrid()] ==> [TEST_99]" );
 };

 /**
  * 01. Data Import  버튼 클릭시 이벤트
  */
 scwin.btn_Import_onclick = function() {
  console.log("[/PD01P01.xml] [btn_Import_onclick()] ==> [TEST_01]");

  var options = {};
  common.gridDataUpload( "grd_list", "csv" ,options);
 };

 /**
  * 저장버튼을 클릭한다.
  */
 scwin.btn_Save_onclick = function() {
  console.log("[/PD01P01.xml] [btn_Save_onclick()] ==> [TEST_02] [dlt_ProGdList.getRowCount()]"+ dlt_ProGdList.getRowCount() );

  for(var i = 0; i < dlt_ProGdList.getRowCount(); i++)
  { 
   dlt_ProGdList.setCellData(i , "AREA_CODE", dma_Search.get("AREA_CODE"));   // 사업장 코드
  }

  if (confirm("저장하시겠습니까?")) {
   common.executeSubmission("sbm_insertPd01Pop");
  }
 };

 /**
  *  저장 콜백 후 자동조회 처리
  */
 scwin.sbm_insertPd01Pop_submitdone = function(e) {
  console.log("[/PD01P03.xml] [sbm_insertPd01Pop_submitdone()] ==> [TEST_01]" );

  alert("저장 되었습니다."); 
 };

 /**
  *  저장 실패 콜백 후 자동조회 처리
  */
 scwin.sbm_insertPd01Pop_submiterr = function(e) {
  console.log("[/PD01P03.xml] [sbm_insertPd01Pop_submiterr()] ==> [TEST_01]" );

  alert("저장에 실패 했습니다. 엑셀 파일에 이미 등록되 파일이 존재하는 지 확인 하세요."); 
 };

 /**
 **
  * 취소버튼을 클릭한다.
  */
 scwin.btn_Cancel_onclick = function() {
  //console.log("[/PD01P03.xml] [btn_Cancel_onclick()] ==> [TEST_01]" );
  dlt_ProGdList.removeAll();
 };

 /**
  * 닫기버튼을 클릭한다.
  */
 scwin.btn_Close_onclick = function() {

  common.closePopup(scwin.popupID);
 };
