	 scwin.fnDrawCalendar = function(sYear, sMonth)
	 { 
		//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_01] [sYear]"+ sYear +"[sMonth]"+ sMonth +"[dlt_Rh01List.CNT]"+ dlt_Rh01List.getRowCount() );

		var start_day, end_dater;
		var day, holiday, memo;
		var sDay = "";
		var aMemo = new Array();
		var aClnmBgcolorAM = new Array();
		var aClnmBgcolorPM = new Array();

		for(var i = 0; i < dlt_Rh01List.getRowCount(); i++)	 	// 날짜별 메모 Ds(ds_CalendarMemo) Loop
		{ 
			aMemo[i] = dlt_Rh01List.getCellData(i, "MMODAT") + dlt_Rh01List.getCellData(i, "TITLE_AM");   // 날짜(20190828) + 메모
			aClnmBgcolorAM[i] = dlt_Rh01List.getCellData(i, "MMODAT") + dlt_Rh01List.getCellData(i, "BGCOLOR_AM");   // 날짜(20190828) + AM 배경색
			aClnmBgcolorPM[i] = dlt_Rh01List.getCellData(i, "MMODAT") + dlt_Rh01List.getCellData(i, "BGCOLOR_PM");   // 날짜(20190828) + PM 배경색 
			//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_12] [i_번째]"+ i +"[02. dlt_Rh01List.CNT]"+ dlt_Rh01List.getRowCount() +"[aClnmBgcolorPM[i]]"+ aClnmBgcolorPM[i] );
		}	 // end fo for()

		var sFirstDate = new Date(sYear, sMonth-1, 1);  		// 이번 달의 첫째 날
		var sLastDate = new Date(sYear, sMonth, 0); 	// 이번 달의 마지막 날  
		var nFirstDateWeek = parseInt(sFirstDate.getDay());      // 이번 달의 첫째 날_요일  
		//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_23] [이번 달의 첫째 날]"+ sFirstDate +"[이번 달의 마지막 날]"+ sLastDate  +"[이번 달의 첫째 날_요일]"+ nFirstDateWeek );

		start_day = nFirstDateWeek;      // 이번 달의 첫째 날_요일[ex: 0:일, 1:월, 2:화...]   
		end_date = parseInt(sLastDate.getDate());      // 이번 달의 마지막 날_일[ex: 31]
		//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_23] [이번 달의 첫째 날_요일]"+ start_day +"[이번 달의 마지막 날_일]"+ end_date );

		var nRow = 0;
	 	var aHolidays = valLib.gfn_GetHolidays(sYear);		// 공휴일 Array   
		//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_41] [aHolidays]"+ aHolidays );

		for(var i = 1, day = start_day; i <= end_date; i++)    // 1부터 말일까지 Loop
		{  
			if ((i + "").length < 2)  sDay = "0" + i;	else  sDay = i;
			var sDateThisMonth = sYear + sMonth + sDay;   // 해당 월의 날짜
			//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_51] [i_번째]"+ i +"[sDateThisMonth]"+ sDateThisMonth +"[메모]" +aMemo );

			if( day == 0 ) 	// 일요일이 존재하면 
			{ 
				grd_list.setCellColor( nRow,  "day"+day,"#FF0000"); 		// 날짜 칼럼 글자색 셋팅(빨간색)
			} 
			else if( day == 6 ) 	// 토요일이 존재하면 
			{
				 grd_list.setCellColor( nRow,  "day"+day,"#2A00FF"); 		// 날짜 칼럼 글자색 셋팅(파간색)
			}

			// 공휴일 및 달력 Data 설정
			holiday = scwin.fn_IsHoliday(sDateThisMonth, aHolidays); 	// 공휴일  ==> TEST @@@ ===>
			//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_52] [i_번째]"+ i +"[sDateThisMonth]"+ sDateThisMonth +"[holiday]" + holiday );

			var memo = scwin.fn_IsMemo(sDateThisMonth, aMemo);	// 메모 
			var slnmBgcolorAM = scwin.fn_IsClnmBgcolor(sDateThisMonth, aClnmBgcolorAM);		// AM 배경색 ==> aClnmBgcolorAM 
			var slnmBgcolorPM = scwin.fn_IsClnmBgcolor(sDateThisMonth, aClnmBgcolorPM);		// PM 배경색 ==> aClnmBgcolorPM 
			//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_53] [i_번째]"+ i +"[day]"+ day +"[nRow]"+ nRow +"[holiday]"+ holiday +"[메모]"+ memo +"[PM 배경색]"+ slnmBgcolorPM );

			if( holiday != "" ) 	// 공휴일이 존재하면 
			{
				//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_61] [i_번째]"+ i +"[nRow]"+ nRow +"[holiday]"+ holiday );
				//dlt_Rh01List.setCellData(nRow, "day"+day, String.fromCharCode(32) + i)  holiday); 		// 날짜 칼럼 셋팅(공휴일명, 날짜)
				dlt_Rh01List.setCellData(nRow, "day"+day, i +"       "+  holiday); 		// 날짜 칼럼 셋팅(날짜, 공휴일명)
				grd_list.setCellColor( nRow,  "day"+day,"#FF0000"); 		// 날짜 칼럼 배경색  셋팅(날짜, 공휴일명) 
			} 
			else 
			{
				//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_62] [i_번째]"+ i +"[nRow]"+ nRow +"[holiday]"+ holiday );
				dlt_Rh01List.setCellData(nRow, "day"+day, i);		// 날짜 칼럼 셋팅(날짜) 
			}
			//console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_71] [i_번째]"+ i +"[nRow]"+ nRow +"[day]"+day );

			if( slnmBgcolorAM != "")		// AM 배경색이 존재하면
			{ 
				grd_list.setCellBackgroundColor(nRow, "memo"+day, slnmBgcolorAM);		// PM 배경색		    
			}
			if( slnmBgcolorPM != "")		// PM 배경색이 존재하면
			{ 
				grd_list.setCellBackgroundColor(nRow, "clnmBgcolor"+day, slnmBgcolorPM);		// PM 배경색		   
				//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_73] [i_번째]"+ i +"[day]"+ day +"[slnmBgcolorPM]"+ slnmBgcolorPM );
			}

			if( memo != "")	// 메모가 존재하면
			{ 
				dlt_Rh01List.setCellData(nRow, "memo"+day, memo);		// 메모 칼럼 셋팅(메모) 			   
				//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_73] [i_번째]"+ i +"[day]"+ day +"[메모]"+memo );
			}

			if(nRow == 4)	 // 줄이 4줄이면 ==> 높이 줄이기
			{
				//this.div_Main.form.grdCal.set_height(593);
			}
			else
				//this.div_Main.form.grdCal.set_height(704);
			console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_90] [day]"+ day );

			// 달력 Data Row추가
			day = (day+1)%7;	 // 시작 요일 변경 
	 		//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_91] [nRow]"+ nRow +"[day]"+ day +"[i]"+  i +"[end_date]"+ end_date );

			if( day == 0 && i != end_date )	  // 요일이 일요일이고 i가 말일이 아니면 ==> 줄 바꿈
			{  
				nRow = nRow + 1;
				dlt_Rh01List.insertRow();  
				//console.log("[/RH01.xml] [fnDrawCalendar()] ==> [TEST_93]  [줄 바꿈 처리 완료] [nRow]"+ nRow +"[day]"+ day +"[i]"+  i +"[end_date]"+ end_date ); 
			}
		}  // end fo for() 
	};

	/************************************
	* Even : aClnmBgcolor
	* Desc : 배경색이 있는지 확인
	****************************************/
	scwin.fn_IsClnmBgcolor = function(sDate, aClnmBgcolor)
	{ 
		//console.log("[/RH01.xfdl] [fn_IsClnmBgcolor()] ==> [TEST_01] [AM 배경색이 확인] [sDate]"+ sDate +"[aClnmBgcolor]"+ aClnmBgcolor +"[aClnmBgcolor.length]"+ aClnmBgcolor.length );

		var rtnMemo = ""; 
		var subMemo = new Array();

		for(var i = 0; i < aClnmBgcolor.length; i++)
		{ 
			if( aClnmBgcolor[i].substr(0,8) == sDate )	  // 메모 작성일자와 날짜가 같으면
			{ 
					rtnMemo += aClnmBgcolor[i].substr(8) + String.fromCharCode(13) + String.fromCharCode(10); 
			}
		}
		//console.log("[/RH01.xfdl] [fn_IsClnmBgcolor()] ==> [TEST_91] [rtnMemo]"+ rtnMemo );

		return rtnMemo;
	};

	/************************************
	* Even : fn_IsMemo
	* Desc : 메모가 있는지 확인
	****************************************/
	scwin.fn_IsMemo = function(sDate, aMemo)
	{ 
		//console.log("[/RH01.xfdl] [fn_IsMemo()] ==> [TEST_01] [메모가 있는지 확인] [sDate]"+ sDate +"[aMemo]"+ aMemo +"[aMemo.length]"+ aMemo.length );

		var rtnMemo = "";
		var chkForYn = "";
		var subMemo = new Array();

		for(var i = 0; i < aMemo.length; i++)
		{
			if(chkForYn == "Y") break;

			if( aMemo[i].substr(0,8) == sDate )	  // 메모 작성일자와 날짜가 같으면
			{
				if(aMemo[i].substr(8).length > 10)
				 {
					subMemo[i] = aMemo[i].substr(8).substr(0,10) + '...';
					rtnMemo += subMemo[i] + String.fromCharCode(13) + String.fromCharCode(10);
				}
				else 
				{
					rtnMemo += aMemo[i].substr(8) + String.fromCharCode(13) + String.fromCharCode(10);
				}
			} else if(chkForYn == "N") {
				chkForYn = "Y";
			}
		}
		//console.log("[/RH01.xfdl] [fn_IsMemo()] ==> [TEST_91] [rtnMemo]"+ rtnMemo );

		return rtnMemo;
	};

	/******************************************
	 * Even : fn_IsHoliday
	 * Desc : 날짜에 공휴일이 있는지 확인
	 ******************************************/
	scwin.fn_IsHoliday = function(sDate, aHolidays)
	{ 
		//console.log("[/RH01.xfdl] [fn_IsHoliday()] ==> [TEST_01] [날짜에 공휴일이 있는지 확인] [sDate]"+ sDate +"[aHolidays]"+ aHolidays +"[aHolidays.length]"+ aHolidays.length );

		var sRtn = "";

		for(var i = 0 ; i < aHolidays.length ; i++)
		{
			if( aHolidays[i].substr(0,8) == sDate )
			{
				sRtn = aHolidays[i].substr(8);
			}
		}
		//console.log("[/RH01.xfdl] [fn_IsMemo()] ==> [TEST_91] [sDate]"+ sDate +"[sRtn]"+ sRtn );

		return sRtn;	
	};

