			if ((i + "").length < 2) sDay = "0" + i;
			var sDateThisMonth = sYear + sMonth + sDay;   // 해당 월의 날짜
			console.log("[/RH01.xml] [n_DrawCalendar()] ==> [TEST_51] [i_번째]"+ i +"[sDateThisMonth]"+ sDateThisMonth +"[메모]" +aMemo );

			// 휴일 및 달력 Data 설정
