head.script:
        scwin.onpageload = function() {
        	 console.log("[/scheduleCalendar.xml] [onpageload()] ==> [TEST_01]");  // 

    		if (!scwin.promise) {
    			scwin.promise = {};
    		}
		};

        scwin.onpageunload = function() {

        };

		/**
		 * 
		 */
		scwin.trigger1_onclick = function(e) {
			console.log("[/scheduleCalendar.xml] [trigger1_onclick()] ==> [TEST_01]");

			var obj = {
				"id":"test123",
				"title":"test123",
				"start":"20190820",
				"end":"20190821"
			};
			schdule1.addSchedule(obj);
		};

		/**
		 * 
		 */
		scwin.trigger2_onclick = function(e) {
			console.log("[/scheduleCalendar.xml] [trigger2_onclick()] ==> [TEST_01]");

			schdule1.removeschedule("test123");
		};

		/**
		 * 
		 */
		scwin.trigger3_onclick = function(e) {
			console.log("[/scheduleCalendar.xml] [trigger3_onclick()] ==> [TEST_01]");

			/*headerLeftBtn 왼쪽 위 (오늘)
			headerRightBtn:false 오른쪽 위(주일월)
			headerTitle:true 년월 */
			var opt = {headerLeftBtn:true, headerRightBtn:false, headerTitle:true };
			schdule1.setHeaderBtn(opt);
		};

		/**
		 * 
		 */
		scwin.trigger4_onclick = function(e) {
			console.log("[/scheduleCalendar.xml] [trigger4_onclick()] ==> [TEST_01]");

			var opt = {
				"sun" : {
					"header" : {
						"color" : "#ff0000"
					},
					"body" : {
						"background-color":"#ff0000", "opacity" : "0.2"
					},
					"num" : {
						"color" : "#ff0000"
					}
				}, "sat" : {
					"header" : {
						"color" : "#0000ff"
					},
					"body" : {
						"background-color":"#ff0000", "opacity" : "0.2"
					},
					"num" : { 
						"color" : "#ff0000"
					}
				}
			};
			schdule1.setDayNameStyle(opt);
		};
		//schdule1.setHeaderBtn({headerLeftBtn:false,headerRightBtn:false,headerTitle:false});

	scwin.schdule1_onclick = function(retObj) {
		//debugger;
	};

    scwin.schdule1_onheaderbtnclick = function(retObj) {
    	console.log("[/scheduleCalendar.xml] [schdule1_onheaderbtnclick()] ==> [TEST_01]");

        /*
        * retObj는 오른쪽 상단(월/주/일) 을 클릭했을 때와
        * 현재 달력모양이 '일'인경우 이전,다음 버튼을 클릭했을 때 발생합니다.
        * 
        * 오른쪽 상단 버튼을 클릭했을 때 retObj는 
        * buttonType, viewType 를 가지며,
        * 왼쪽 상단 버튼을 클릭했을 때 retObj는
        * buttonType, date 를 가지고 있습니다.
        */
        console.log(retObj);
        if(retObj) {
            if(retObj.date) {
                alert(retObj.buttonType + " " + retObj.date);
            } else if(retObj.viewType) {
                alert(retObj.viewType);
            }
        }
    };
