	/**
	 * 다운로드된 파일 목록 생성
	 */ 
	scwin.fn_InsertGenerator = function() {
		console.log("[/upload.xml] [fn_InsertGenerator()] ==> [TEST_01] [다운로드된 파일 목록 생성]" );

 		var cnt =  dlt_fileList.getRowCount();		// 파일 정보 Ds
	 	console.log("[/upload.xml] [fn_InsertGenerator()] ==> [TEST_02] [cnt]"+ cnt );

 		 if(cnt != 0)			// 첨부된 파일이 존재하면
 		 { 
	 		for(i = 0; i < dlt_fileList.length; i++)
	 		{
	 		  	var fileNm = dlt_fileList.getCellData(i, "AN_FILE_ORG");		// 원본 파일명
	 			var fileId = dlt_fileList.getCellData(i, "AN_FILE_ID");			// 업로드 파일

	 		  	gen_file.insertChild(i);			// 첨부된 파일명
	 		  	gen_file.getChild(i, "btn_download").setValue(fileNm) ;			// 첨부된 파일명
	 		  	gen_file.getChild(i, "btn_download").setTitle() ;
	 		 }	 // end of for()
 		 } 
	};

 	/**
	 * 파일 선택 후 Grid를 통해 파일 정보를 표현 
