 		 { 
			mpd1.removeAllFiles(); 
			rerurn; 
		}

 		if(mpd1.getFileCount() > 0)
 		 { 
 		 	mpd1.startUpload; 		// 파일 업로드 처리(임시 폴더에 파일 업로드)
 		 }
	};

	/**
	 * 파일 등록 후 콜백
	 */ 
	scwin.mpd1_ondone = function(data) {
		console.log("[/upload.xml] [mpd1_ondone()] ==> [TEST_01] [2. 파일 등록 후 콜백]" );

		for(var i = 0; i< data.length; i++)
		{    
			data[i].isNew= "Y";		// 신규 여부
			data[i].AN_FILE_ID = data[i].file;				// 업로드 파일
			data[i].AN_FILE_ORG = storedFileList;		// 원본 파일명
			data[i].AN_FILE_EXG = scwin.fn_getExtensionOfFileNm(data[i].localFile) ;		// 파일 확장자명
			data[i].AN_FILE_SIZE = fileSizeList;			// 파일 크기 
		} // end of for()

		dlt_fileList.setJSON(data, true);	
		dlt_fileList.removeColumnFilterAll();	
		dlt_fileList.setColumnFilter({type:'row', colIndex:'status', key:'R', condition:'and'});
	};

 	/**
	 * 파일 확장자 가져오기 
	 */ 
	scwin.fn_getExtensionOfFileNm = function() {

		var fileLen = fileNm.length; 
		var lastDot = fileNm.lastIndexOf('.'); 
		var fileExt = fileNm.substring(lastDot, fileLen).toLowerCase();

		return fileLen; 
	};

	/**
	 * 파일 확장자 체크 
	 */ 
	scwin.fn_checkExtension = function(fileExt) {

		var result;		
		var arrExt = new Array(".ppt", ".zip");

		// 배열 요소를 검사하여 체크 
		for(var i =0; i< arrExt.length; i++) 
		{ 
			if(arrExt[i] == fileExt) 
			{ 
				result = true; 
				break; 
			} 
			else 
			{			
				result = false; 
			}
		}  // end of for() 

		return result;
	};

	/**
	 * 첨부된 파일 가져오기 콜백  
