	/**
	 * Grid 클릭 이벤트
	 */
	scwin.grd_fileList_oncellclick = function(row,col) {
		console.log("[/upload.xml] [grd_fileList_oncellclick()] ==> [TEST_01] [1. Grid 클릭 이벤트]" );

		if(col == 2)			//  삭제 버튼 클릭이면
		{
			dlt_fileList.setCellData(row, "rownum", dlt_fileList.getRowPostion()); 
			dma_anyInfo.setJSON(dlt_fileList.getCellDataon(row)); 

			com.executeSubmission( "sbm_deleteFileTemp" );			// 첨부된 파일 삭제
		} 
	};

	/**
	 * 첨부된 파일 삭제 처리 콜백 
	 */	
	scwin.sbm_deleteFileTemp_submitdone = function(e) {
		console.log("[/upload.xml] [sbm_deleteFileTemp_submitdone()] ==> [TEST_01] [첨부된 파일 삭제 처리 콜백]" );

		var statusCode =  com.getParameterCode();	 // 상태 코드
		console.log("[/upload.xml] [sbm_deleteFileTemp_submitdone()] ==> [TEST_02] [상태 코드]"+ statusCode );

		if(statusCode =="S")  		// 성공이면
		{
			var data = e.responseJSON.data;		// 파일 정보 Ds
			var row = data.rwonum;
			var isNew = dlt_fileList.getCellData(row, "isNew"); 		// 신규 여부

			if(isNew == "")  		// 파일 서버에 등록된 파일을 삭제한 경우
			{
				dlt_fileList.setCellData(row, "delYn", "Y"); 		// 삭제
				dlt_fileList.setCellData(row, "status", "Y"); 		// 상태 
				dlt_fileList.setColumnFilter({type:'row', colIndex:'status', key:'R', condition:'and'});
				dlt_fileList.setCellData(row, "status", "Y"); 		// 상태
			}
			else if(isNew == "Y")  		// 신규 이면
			{
				dlt_fileList.removeRow(row);
		 	}
		} 
	};

	/**
	 * 첨부된 파일 유무 Formatter 처리
	 */	
	scwin.attachFormatter = function(data, fData, rowIndex, colIndex) {
		console.log("[/upload.xml] [attachFormatter()] ==> [TEST_01] [첨부된 파일 유무 Formatter 처리]" );

		var anSeq = dlt_resAnnounce.getCellData(rowIndex, "AN_FILE_SEQ"); 		// SEQ
		var attach = dlt_fileList.getMatchedColumnData("AN_FILE_SEQ", anSeq, "AN_FILE_ID"); 		// dlt_fileList Ds와 일련번호가 일치하는 ID
		console.log("[/upload.xml] [attachFormatter()] ==> [TEST_02] [anSeq]"+ anSeq +"[attach]"+ attach );

		if(attach.length > 0)  		//  일련번호가 일치하는 ID가 존재하면
		{
			grd_resAnnounce.setCellClass(rowIndex, colIndex, "ico_file"); 		// grd_resAnnounce Grid에 있는 첨부 이미지 보이기 
		}
		else
		{
			return "";
	 	} 
	};


