	   			str = str + "\r\n"+i+"번째 localFile==="+data[i].localFile;
	   			str = str + "\r\n"+i+"번째 maxUploadSize==="+data[i].maxUploadSize;
	   			str = str + "\r\n"+i+"번째 size==="+data[i].size; 
		}	

   		textarea1.setValue( textarea1.getValue() +str );	
