mapper.update:
UPDATE SP_SAMPLE
SET SAMPLE_NM = #{SAMPLE_NM}, PARENT_SAMPLE_CD = #{PARENT_SAMPLE_CD}, SAMPLE_LEVEL = #{SAMPLE_LEVEL}
, SORT_ORDER = #{SORT_ORDER}, IS_USE = #{IS_USE}, FILE_PATH = #{FILE_PATH}, CONTENT = #{CONTENT}
, KEYWORD = #{KEYWORD}, NOTE = #{NOTE}
WHERE SAMPLE_CD = #{SAMPLE_CD}

mapper.select:
SELECT id
	 , SEQ
	 , ORDER_YEAR
	 , ORDER_NO
	 , CUST_ID
	 , CUST_NM
	 , CUST_TEL_NO
	 , CUST_EMAIL
	 , CUST_GENDER
	 , SHIP_TO_ADDR
	 , SHIP_TO_SIDO
	 , SHIP_TO_SIDOCD
	 , SHIP_TO_SIGUNGU
	 , SHIP_TO_SIGUNGUCD
	 , SHIP_TO_STREETCD
	 , SHIP_TO_STREET
	 , PRDT_CNTRY
	 , PRDT_CNTRYCD
	 , PRDT_CMPNY_NM
	 , PRDT_NM
	 , CONVERT(PRDT_BLOB USING utf8) AS PRDT_BLOB
	 , TRACK_NM
	 , TRACK_IMG_SRC
	 , TRACK_SITE
	 , COST
	 , DISC_COST
	 , DISC
	 , CNT
	 , POINT_RATE
	 , POINT
	 , TRACK_DATE
	 , TRACK_TIME
	 , CUST_REGT_NO
	 , PAY_CREDIT_TYPE
	 , PAY_CREDIT_IMG
  FROM SP_CUSTOMER_ORDER
 WHERE 1=1 

   AND id = #{id}


   AND SEQ = #{SEQ}


   AND ORDER_YEAR = #{ORDER_YEAR}


   AND ORDER_NO = #{ORDER_NO}


   AND CUST_ID = #{CUST_ID}


   AND CUST_NM LIKE concat('%',#{CUST_NM},'%')


   AND CUST_TEL_NO = #{CUST_TEL_NO}


   AND CUST_EMAIL = #{CUST_EMAIL}


   AND CUST_GENDER = #{CUST_GENDER}


   AND SHIP_TO_ADDR LIKE concat('%',#{SHIP_TO_ADDR},'%')


   AND SHIP_TO_SIDO LIKE concat('%',#{SHIP_TO_SIDO},'%')


   AND SHIP_TO_SIGUNGU LIKE concat('%',#{SHIP_TO_SIGUNGU},'%')


   AND SHIP_TO_SIGUNGUCD = #{SHIP_TO_SIGUNGUCD}


   AND SHIP_TO_STREETCD = #{SHIP_TO_STREETCD}


   AND SHIP_TO_STREET LIKE concat('%',#{SHIP_TO_STREET},'%')


   AND PRDT_CNTRY = #{PRDT_CNTRY}


   AND PRDT_CNTRYCD = #{PRDT_CNTRYCD}


   AND PRDT_CMPNY_NM = #{PRDT_CMPNY_NM}


   AND PRDT_NM = #{PRDT_NM}


   AND PRDT_BLOB = #{PRDT_BLOB}


   AND TRACK_NM = #{TRACK_NM}


   AND TRACK_IMG_SRC = #{TRACK_IMG_SRC}


   AND TRACK_SITE = #{TRACK_SITE}


   AND COST = #{COST}


   AND DISC_COST = #{DISC_COST}


   AND DISC = #{DISC}


   AND CNT = #{CNT}


   AND POINT_RATE = #{POINT_RATE}


   AND POINT = #{POINT}


   AND TRACK_DATE = #{TRACK_DATE}


   AND TRACK_TIME = #{TRACK_TIME}


   AND CUST_REGT_NO = #{CUST_REGT_NO}


   AND PAY_CREDIT_TYPE = #{PAY_CREDIT_TYPE}


   AND PAY_CREDIT_IMG = #{PAY_CREDIT_IMG}

mapper.insert:
INSERT INTO SP_CUSTOMER_ORDER
	  (id
	, SEQ
	, ORDER_YEAR
	, ORDER_NO
	, CUST_ID
	, CUST_NM
	, CUST_TEL_NO
	, CUST_EMAIL
	, CUST_GENDER
	, SHIP_TO_ADDR
	, SHIP_TO_SIDO
	, SHIP_TO_SIDOCD
	, SHIP_TO_SIGUNGU
	, SHIP_TO_SIGUNGUCD
	, SHIP_TO_STREETCD
	, SHIP_TO_STREET
	, PRDT_CNTRY
	, PRDT_CNTRYCD
	, PRDT_CMPNY_NM
	, PRDT_NM
	, PRDT_BLOB
	, TRACK_NM
	, TRACK_IMG_SRC
	, TRACK_SITE
	, COST
	, DISC_COST
	, DISC
	, CNT
	, POINT_RATE
	, POINT
	, TRACK_DATE
	, TRACK_TIME
	, CUST_REGT_NO
	, PAY_CREDIT_TYPE
	, PAY_CREDIT_IMG)
VALUES(
	  (SELECT (MAX(id)+1) FROM SP_CUSTOMER_ORDER AS SP_ORDER)
	, (SELECT (MAX(id)+1) FROM SP_CUSTOMER_ORDER AS SP_ORDER)
	, #{ORDER_YEAR}
	, #{ORDER_NO}
	, #{CUST_ID}
	, #{CUST_NM}
	, #{CUST_TEL_NO}
	, #{CUST_EMAIL}
	, #{CUST_GENDER}
	, #{SHIP_TO_ADDR}
	, #{SHIP_TO_SIDO}
	, #{SHIP_TO_SIDOCD}
	, #{SHIP_TO_SIGUNGU}
	, #{SHIP_TO_SIGUNGUCD}
	, #{SHIP_TO_STREETCD}
	, #{SHIP_TO_STREET}
	, #{PRDT_CNTRY}
	, #{PRDT_CNTRYCD}
	, #{PRDT_CMPNY_NM}
	, #{PRDT_NM}
	, #{PRDT_BLOB}
	, #{TRACK_NM}
	, #{TRACK_IMG_SRC}
	, #{TRACK_SITE}
	, #{COST}
	, #{DISC_COST}
	, #{DISC}
	, #{CNT}
	, #{POINT_RATE}
	, #{POINT}
	, #{TRACK_DATE}
	, #{TRACK_TIME}
	, #{CUST_REGT_NO}
	, #{PAY_CREDIT_TYPE}
	, #{PAY_CREDIT_IMG})

