mapper.select:
SELECT SEQ, TITLE, CONTENT, CAST(CREATED_DATE AS CHAR(10)) AS CREATED_DATE
FROM SP_RELEASE_INFO
WHERE IS_USE = #{IS_USE} 


		ORDER BY SEQ DESC


		ORDER BY SEQ ASC


LIMIT #{START_NUM} , #{END_NUM}

mapper.update:
UPDATE SP_RELEASE_INFO
   SET TITLE = #{TITLE}, CONTENT = #{CONTENT}, CREATED_DATE = #{CREATED_DATE}
WHERE SEQ = #{SEQ}
